02432 void SteppingAction::FillNtuple1(G4double PosX, G4double PosY, G4double PosZ, G4int reasonID, G4int EveryVol, G4int FissionVol, G4int CaptureVol, G4int InelasticVol, G4int nnPrimeVol, G4int nXnVol, G4int pID, G4int OutPhysVol) {
02433 
02434     auto analysisManager = G4AnalysisManager::Instance();
02435     analysisManager->FillNtupleFColumn( 0, 0,PosX);          // ID= 0 PosX en mm
02436     analysisManager->FillNtupleFColumn( 0, 1,PosY);          // ID= 1 PosY en mm
02437     analysisManager->FillNtupleFColumn( 0, 2,PosZ);          // ID= 2 PosZ en mm
02438     analysisManager->FillNtupleIColumn( 0, 3,reasonID);      // ID= 3 ID of the scoring
02439     analysisManager->FillNtupleIColumn( 0, 4,EveryVol);      // ID= 4 Number of neutrons in the volume of interest
02440     analysisManager->FillNtupleIColumn( 0, 5,FissionVol);    // ID= 5 Number of neutrons inducing fission into the volume
02441     analysisManager->FillNtupleIColumn( 0, 6,CaptureVol);    // ID= 6 Number of neutrons captured         into the volume
02442     analysisManager->FillNtupleIColumn( 0, 7,InelasticVol);  // ID= 7 Number of neutrons inducing inelastic scattering into the volume
02443     analysisManager->FillNtupleIColumn( 0, 8,nnPrimeVol);    // ID= 8 Number of neutrons inducing inelastic scattering into the volume
02444     analysisManager->FillNtupleIColumn( 0, 9,nXnVol);        // ID= 9 Number of neutrons inducing inelastic scattering into the volume
02445     analysisManager->FillNtupleIColumn( 0,10,pID);           // ID=11 ID of the parent track
02446     analysisManager->FillNtupleIColumn( 0,11,fNeutronGeneration); // ID=10 Neutron generation number
02447     analysisManager->FillNtupleIColumn( 0,12,OutPhysVol);    // ID=12 Number of neutrons going out of the volume
02448     // analysisManager->FillNtupleDColumn( 0,13,Edep);       // ID=13 Kinetic energy in eV
02449 //     analysisManager->FillNtupleIColumn( 0, 7,InPhysVol);     // ID= 7 Number of neutrons entering  in the volume
02450 //     analysisManager->FillNtupleDColumn( 0,14,GlobalTime);    // ID=14 Time since the track is created in ns
02451 //     analysisManager->FillNtupleDColumn( 0,15,StepLength);    // ID=15 Neutron step length in cm
02452 //     analysisManager->FillNtupleIColumn( 0,16,fNeutronGeneration);     // ID=16 Neutron generation number
02453     analysisManager->AddNtupleRow(0);
02454 }
