02456 void SteppingAction::FillNtuple2(G4double PosX, G4double PosY, G4double PosZ, G4double energyK, G4int FissionVol, G4int CaptureVol, G4double StepLength, G4double CubicVolume, G4int A, G4int Z) {
02457 
02458     auto analysisManager = G4AnalysisManager::Instance();
02459     analysisManager->FillNtupleFColumn( 1, 0,PosX);          // ID= 0 PosX en mm
02460     analysisManager->FillNtupleFColumn( 1, 1,PosY);          // ID= 1 PosY en mm
02461     analysisManager->FillNtupleFColumn( 1, 2,PosZ);          // ID= 2 PosZ en mm
02462     analysisManager->FillNtupleDColumn( 1, 3,energyK);       // ID= 3 Kinetic energy in eV
02463     analysisManager->FillNtupleDColumn( 1, 4,StepLength/CubicVolume);    // ID=4 Neutron step length in cm
02464     analysisManager->FillNtupleIColumn( 1, 5,FissionVol);    // ID= 9 Number of neutrons inducing fission into the volume
02465     analysisManager->FillNtupleIColumn( 1, 6,CaptureVol);    // ID=10 Number of neutrons captured         into the volume
02466     analysisManager->FillNtupleIColumn( 1, 7,fNeutronGeneration);     // ID=16 Neutron generation number
02467     analysisManager->FillNtupleIColumn( 1, 8,A);  // ID=11 Number of neutrons inducing inelastic scattering into the volume
02468     analysisManager->FillNtupleIColumn( 1, 9,Z);    // ID=12 Number of neutrons inducing inelastic scattering into the volume
02469     analysisManager->AddNtupleRow(1);
02470 }
