02472 void SteppingAction::FillNtupleDepositedPower(const G4Step* theStep) {
02473     if (theStep && theStep->GetTotalEnergyDeposit()) {
02474         G4double N0_Energy = theStep->GetTotalEnergyDeposit()/eV;
02475         if (N0_Energy > 0.) {
02476             G4int row = 2;
02477             if (theStep->GetPostStepPoint() &&
02478                 theStep->GetPostStepPoint()->GetPhysicalVolume()
02479             ) {
02480                 G4String thePostPVname = theStep->GetPostStepPoint()->GetPhysicalVolume()->GetName();
02481                 G4ThreeVector position = theStep->GetPostStepPoint()->GetPosition();
02482                 auto analysisManager = G4AnalysisManager::Instance();
02483                 analysisManager->FillNtupleFColumn(row, 0, position.x()/mm);    // ID= 0 X Position
02484                 analysisManager->FillNtupleFColumn(row, 1, position.y()/mm);    // ID= 1 Y Position
02485                 analysisManager->FillNtupleFColumn(row, 2, position.z()/mm);    // ID= 2 Z Position
02486                 analysisManager->FillNtupleDColumn(row, 3, N0_Energy);          // ID= 3   Energy
02487                 analysisManager->FillNtupleIColumn(row, 4, thePostPVname.find("FuelPellet") != std::string::npos ? 1 : 0);
02488                 analysisManager->AddNtupleRow(row);
02489             }
02490         }
02491     }
02492 }
