02524 void SteppingAction::FillNdensityVStimeHisto(double time) {
02525 
02526     auto analysisManager = G4AnalysisManager::Instance();
02527     f_tBin = time;
02528     if( IsTargetRegion (fPhysicalVolumeNumber)) { analysisManager->FillH1(21, log10(time)); }  // Target    region
02529     if( IsCoolantRegion(fPhysicalVolumeNumber)) { analysisManager->FillH1(22, log10(time)); }  // coolant   region
02530     if( IsFuel         (fPhysicalVolumeNumber)) { analysisManager->FillH1(23, log10(time)); }  // fuel      region
02531     if( IsReflector    (fPhysicalVolumeNumber)) { analysisManager->FillH1(24, log10(time)); }  // Reflector region
02532 }
