02545             analysisManager->FillH1(1, fPhysicalVolumeNumber, Tot_Edep );                    // Energy deposit      in listed volumes
02546             analysisManager->FillH1(18,fPhysicalVolumeNumber, Tot_Edep*Tot_Edep ); // Energy deposit^2 in listed volumes
02547             analysisManager->FillH1(25,fPhysicalVolumeNumber);                                          // Number of times the deposited energy is scored
02548 
02549 
02550 
02551             //------------------------------------------------------------------*
02552             // Special Case for TMXTarget
02553             /*
02554             G4double zMin = p->minZ, zMax = p->maxZ;
02555             // double nBins = p->nbinsZ;
02556             G4double radialWidth = 12.5 * mm;
02557             G4double xPosition = theStep->GetPostStepPoint()->GetPosition().x()/mm;
02558             G4double yPosition = theStep->GetPostStepPoint()->GetPosition().y()/mm;
02559             G4double zPosition = theStep->GetPostStepPoint()->GetPosition().z()/mm;
02560             if (zMin <= zPosition && zPosition <= zMax) {
02561                 if (
02562                     theStep->GetPostStepPoint()->GetPhysicalVolume() &&
