02567                     int leadHisto = (int)(rPosition/radialWidth);
02568                     std::string logicalVolumeName = theStep->GetPostStepPoint()->GetPhysicalVolume()->GetLogicalVolume()->GetName();
02569                     if (logicalVolumeName == "lWindow") { // Beam Window
02570                         analysisManager->FillH1(48, zPosition, Tot_Edep);
02571                     }
02572                     else if (logicalVolumeName == "lTargetCoolant" && rPosition <= 95.*mm) {
02573                         analysisManager->FillH1(49+leadHisto, zPosition, Tot_Edep);
02574                     }
02575                 }
02576             }
02577             */
02578             //------------------------------------------------------------------*
02579         }
02580     }
02581 }
