02588 void SteppingAction::FillEdepPrimaries(const G4Step* theStep/*G4ThreeVector& PostPos, double Tot_Edep*/, double /*dbl_Ibeam*/, G4AnalysisManager& analysisManager) {
02589     G4double Tot_Edep = theStep->GetTotalEnergyDeposit()/eV; // Total Energy Deposit in this step in eV
02590     G4ThreeVector  PrePos = theStep->GetPreStepPoint()->GetPosition();
02591     G4ThreeVector PostPos = theStep->GetPostStepPoint()->GetPosition();
02592     if( PostPos.z() > fTargetZdistanceToBottom - fh0 - 126*mm && PostPos.z() < fTargetZdistanceToBottom - fh0 + 126*mm ) { // && Tot_Edep > 0
02593         double MeshCubicVolume = ((p->maxX-p->minX)/p->nbinsX * (p->maxY-p->minY)/p->nbinsY)/cm2 * 25.2;
02594         analysisManager.FillH2(5, PostPos.x()/mm, PostPos.y()/mm, Tot_Edep  /MeshCubicVolume ); // Energy deposit mapping for primary protons
02595     }
02596     // FillNtupleDepositedPower(theStep); // Already taken into account in line 119
02597 }
02598 
02599 void SteppingAction::AccumulateMeanEperFission(const G4Step* theStep) { // To calculate the mean deposited energy per fission
02600     int      ID_track = theStep->GetTrack()->GetTrackID();
02601     G4double Tot_Edep = theStep->GetTotalEnergyDeposit()/eV; // Total Energy Deposit in this step in eV
02602 
02603     if( fID_firstfission > ID_track) {  // If secondaries of fission nfID_firstfission are all simulated
02604         // Calculation of the mean energy per fission
02605         fEventAction->AddEperfission(fEperFission/(G4double)fNfission_fromFission);
02606 
02607         // Initialization of variables
02608         fFromFission = false;
02609         fID_firstfission = 0;
02610         fEperFission = 0;
02611         fNfission_fromFission = 0;
02612     }
02613     else if (fFromFission) {
02614         fEperFission += Tot_Edep;
02615     }
02616 
02617     // WARNING: not necessary (to be tested)
02618     if ( theStep->GetTrack()
02619       && theStep->GetTrack()->GetParentID() == 0
02620       && theStep->GetTrack()->GetCurrentStepNumber()==1 ) { // New event -> Initialization
02621         fFromFission = false;
02622         fID_firstfission = 0;
02623         fNfission_fromFission = 0;
02624         fEperFission = 0;
02625     }
02626 }
02627 
02628 void SteppingAction::FillTimeEnergyFissionProducts(const G4Step* theStep) {
02629     auto analysisManager = G4AnalysisManager::Instance();
02630     G4String processName = "";
02631     if(theStep->GetPostStepPoint()->GetProcessDefinedStep()) {
02632         processName = theStep->GetPostStepPoint()->GetProcessDefinedStep()->GetProcessName();}
02633 
02634     if(processName != "ionIoni" && processName != "ParallelScoringWorld" && processName != "ParallelScoringWorld_polar" && processName!="RadioactiveDecayBase" && processName!="CoupledTransportation" && processName!="hadElastic" && processName!="alphaInelastic" && processName!="msc" && processName!="hIoni" && processName != "Transportation") {
02635         // std::cout << processName << std::endl;
02636         return;
02637     }
02638 //         G4int A = particleType->GetAtomicMass();
02639 //         G4int Z = particleType->GetAtomicNumber();
02640     const std::vector<const G4Track*> *secondaries = theStep->GetSecondaryInCurrentStep();
02641 //         bool Presence_anti_nu_e = false;
02642 //         G4double AntiNuGlobalTime = 0, AntiNuEnergy = 0;
02643 //         bool Presence_e = false;
02644 //         G4double ElectronGlobalTime = 0, ElectronEnergy = 0;
02645     for(G4int i=0; i<(G4int)secondaries->size(); ++i) {
02646         G4String secondary_particleName = (*secondaries)[i]->GetDefinition()->GetParticleName();
02647         if (secondary_particleName == "neutron") {
02648             analysisManager->FillH2(28, std::log10((*secondaries)[i]->GetGlobalTime()/s), std::log10((*secondaries)[i]->GetKineticEnergy()/eV)); // Energy and time for FF secondaries
02649         }
02650         else if (secondary_particleName == "gamma" && processName=="RadioactiveDecayBase") {
02651             analysisManager->FillH2(29, std::log10((*secondaries)[i]->GetGlobalTime()/s), std::log10((*secondaries)[i]->GetKineticEnergy()/eV)); // Energy and time for FF secondaries
02652         }
02653         else if (secondary_particleName == "e-" && processName=="RadioactiveDecayBase") {
02654 //                 ElectronGlobalTime = std::log10((*secondaries)[i]->GetGlobalTime()/s);
02655 //                 ElectronEnergy     = std::log10((*secondaries)[i]->GetKineticEnergy()/eV);
02656 //                 Presence_e = true;
02657             analysisManager->FillH2(24, std::log10((*secondaries)[i]->GetGlobalTime()/s), std::log10((*secondaries)[i]->GetKineticEnergy()/eV)); // Energy and time for FF secondaries
02658         }
02659         else if (secondary_particleName == "anti_nu_e" && processName=="RadioactiveDecayBase") {
02660 //                 AntiNuGlobalTime = std::log10((*secondaries)[i]->GetGlobalTime()/s);
02661 //                 AntiNuEnergy     = std::log10((*secondaries)[i]->GetKineticEnergy()/eV);
02662 //                 Presence_anti_nu_e = true;
02663             analysisManager->FillH2(31, std::log10((*secondaries)[i]->GetGlobalTime()/s), std::log10((*secondaries)[i]->GetKineticEnergy()/eV)); // Energy and time for FF secondaries
02664         }
02665         else if (processName=="RadioactiveDecayBase" && (*secondaries)[i]->GetDefinition()->GetParticleType()!="nucleus") {
02666 //             std::cout << secondary_particleName << std::endl;
02667             analysisManager->FillH2(30, std::log10((*secondaries)[i]->GetGlobalTime()/s), std::log10((*secondaries)[i]->GetKineticEnergy()/eV)); // Energy and time for FF secondaries
02668         }
02669     }
02670 //         if(Presence_e && Presence_anti_nu_e) {
02671 //             analysisManager.FillH2(24, ElectronGlobalTime, ElectronEnergy); // Energy and time for FF secondaries
02672 //             analysisManager.FillH2(31, AntiNuGlobalTime, AntiNuEnergy); // Energy and time for FF secondaries
02673 //         }
02674 }
