02676 void SteppingAction::ScorePrimaryProtons(const G4Step* theStep, double dbl_Ibeam) {
02677     if(IsPrimaryProton(theStep->GetTrack()->GetDefinition()->GetParticleName(), theStep->GetTrack()->GetTrackID())) { // primary protons
02678         auto analysisManager = G4AnalysisManager::Instance();
02679         G4Track* theTrack = theStep->GetTrack();
02680         const G4int stepnumber = theTrack->GetCurrentStepNumber();
02681         //G4double Tot_Edep = theStep->GetTotalEnergyDeposit()/eV; // Total Energy Deposit in this step in eV
02682         G4ThreeVector  PrePos = theStep->GetPreStepPoint()->GetPosition();
02683         //G4ThreeVector PostPos = theStep->GetPostStepPoint()->GetPosition();*/
02684 
02685         FillEdepPrimaries(theStep/*PostPos, Tot_Edep*/, dbl_Ibeam, *analysisManager);
02686         FillBeamProfile(*theTrack, stepnumber, PrePos, *analysisManager);
02687     }
02688 }
