02729 void SteppingAction::GetNeutronHadronicProcesses() {
02730     // on recupere la particule
02731     G4ParticleDefinition *particle = G4ParticleTable::GetParticleTable()->FindParticle("neutron"); // G4Neutron::Definition();
02732 
02733     // on recupere le process manager pour la particule
02734     G4ProcessManager *pManager = particle->GetProcessManager();
02735     // on recupere la liste des processus
02736     G4ProcessVector *pList = pManager->GetProcessList();
02737     // on boucle pour trouver le bon process (je pense qu'il faut les imprimer une fois pour voir)
02738     const G4VProcess *pp = nullptr;
02739 
02740     for (size_t idx = 0; idx < pList->size(); idx++)
02741     {
02742         pp = (*pList)[idx];
02743         G4int ptype = pp->GetProcessType();
02744         if( ptype == 4 ) { // Reaction of a neutron with something else (no surface, parallel world, etc.)
02745             if     ((G4String)pp->GetProcessName() == "hadElastic") {
02746                 fProcessElas = (G4HadronicProcess *)pp; }
02747             else if((G4String)pp->GetProcessName() == "neutronInelastic") {
02748                 fProcessInel = (G4HadronicProcess *)pp; }
02749             else if((G4String)pp->GetProcessName() == "nCapture") {
02750                 fProcessCap = (G4HadronicProcess *)pp; }
02751             else if((G4String)pp->GetProcessName() == "nFission") {
02752                 fProcessFis = (G4HadronicProcess *)pp; }
02753             else {
02754                 std::cout << "\n\n !! WARNING !! \n !! At least one hadronic process has not been taken into account in SteppingAction::GetNeutronHadronicProcesses !!" << std::endl;
02755                 printf(" [%s,%d,%d]", (const char *)pp->GetProcessName(), ptype, pp->GetProcessSubType());
02756                 std::cout << std::endl;
02757             }
02758         }
02759     }
02760 }
