02762 G4double SteppingAction::GetElementElasticCrossSection(const G4Step * theStep, G4Element* elm, G4Material* mat) {
02763     // on recupere une DynamicParticle
02764     const G4DynamicParticle*    particleDyn = theStep->GetTrack()->GetDynamicParticle();
02765     // On vrifie l'nergie cinltique non nulle sinon on en dfini une autre
02766     G4Track theTrack = *theStep->GetTrack();
02767     G4StepPoint PreStep = *theStep->GetPreStepPoint();
02768     if(particleDyn->GetKineticEnergy() < 1e+6)
02769         particleDyn = new G4DynamicParticle(particleDyn->GetParticleDefinition(), particleDyn->GetMomentumDirection(), KineticEnergy_eV(theStep)*eV);
02770 
02771     // on recupere la section efficace
02772     return fProcessElas->GetElementCrossSection(particleDyn, elm, mat)/cm2;
02773 }
