02870 G4double SteppingAction::GetMaterialElasticCrossSection(const G4Step * theStep, G4Material* mat)  {
02871     // on recupere une DynamicParticle
02872     const G4DynamicParticle*    particleDyn = theStep->GetTrack()->GetDynamicParticle();
02873     // On vrifie l'nergie cinltique non nulle sinon on en dfini une autre
02874     G4Track theTrack = *theStep->GetTrack();
02875     G4StepPoint PreStep = *theStep->GetPreStepPoint();
02876     if(particleDyn->GetKineticEnergy() < 1e+6)
02877         particleDyn = new G4DynamicParticle(particleDyn->GetParticleDefinition(), particleDyn->GetMomentumDirection(), KineticEnergy_eV(theStep)*eV);
02878 
02879     G4double matCrossSection = 0;
02880     G4int nElements = mat->GetNumberOfElements();
02881     const G4double* nAtomsPerVolume = mat->GetVecNbOfAtomsPerVolume();
02882     G4double TotNbAtomsPerVolume = mat->GetTotNbOfAtomsPerVolume();
02883     for(G4int i=0; i<nElements; ++i) {
02884         matCrossSection += nAtomsPerVolume[i]/(G4double)TotNbAtomsPerVolume * (fProcessElas->GetCrossSectionDataStore()->GetCrossSection(particleDyn, (*mat->GetElementVector())[i], mat)/cm2);
02885     }
02886 
02887     // on recupere la section efficace
02888     return matCrossSection;
02889 //     return fProcessElas->GetCrossSectionDataStore()->GetCrossSection(particleDyn, mat/*thePrePV->GetLogicalVolume()->GetMaterial()*/);
02890 }
