02961 void SteppingAction::MCNP_Keff_estimators_ori(const G4Step theStep) {
02962 //     std::cout << "Beginning of MCNP_Keff_estimators" << std::endl;
02963 
02964 //     const G4VProcess *pp = theStep.GetPostStepPoint()->GetProcessDefinedStep();
02965 //
02966 //     if(pp && theStep.GetTrack()->GetDynamicParticle()->GetKineticEnergy() < 20*MeV
02967 //     && pp->GetProcessName() != "hadElastic" && pp->GetProcessName() != "CoupledTransportation"
02968 //     && pp->GetProcessName() != "nFission" && pp->GetProcessName() != "ParallelScoringWorld"
02969 //     && pp->GetProcessName() != "nCapture" /*&& pp->GetProcessSubType() != 121*/) {
02970 //         printf(" [%s,%d,%d] \n", (const char *)((G4NeutronInelasticProcess*)pp)->GetProcessName(), ((G4NeutronInelasticProcess*)pp)->GetProcessType(), ((G4NeutronInelasticProcess*)pp)->GetProcessSubType());
02971 //         const G4ParticleDefinition* part = theStep.GetTrack()->GetDefinition();
02972 //         ((G4NeutronInelasticProcess*)pp)->DumpPhysicsTable(*part);
02973 //         ((G4NeutronInelasticProcess*)pp)->DumpPhysicsTable(*G4Neutron::Definition());
02974 // //     if((const char *)pp->GetProcessName() == "Hadronic" && pp->GetProcessSubType() ==  fHadronInelastic) break; // mais il faut tous les imprimer une fois pour voir!
02975 //     }
02976 
02977     G4double nu = fEventAction->GetNu();
02978 
02979     // Get the post step point
02980     G4StepPoint*  thePostPoint = theStep.GetPostStepPoint();
02981 
02982     // Get the process name
02983     G4String processName = "";
02984     if(thePostPoint->GetProcessDefinedStep()) {
02985         processName = thePostPoint->GetProcessDefinedStep()->GetProcessName();}
02986 
02987     if( processName == "hadElastic" || processName == "neutronInelastic" || processName == "nCapture" || processName == "nFission") {
02988         G4Material* mat = thePostPoint->GetPhysicalVolume()->GetLogicalVolume()->GetMaterial();
02989         G4double MaterialSigmaF = GetMaterialFissionCrossSection(&theStep, mat);
02990     if(MaterialSigmaF == 0) return;
02991         G4double MaterialSigmaTot = GetMaterialElasticCrossSection(&theStep, mat)
02992                                   + GetMaterialInelastCrossSection(&theStep, mat)
02993                                   + GetMaterialCaptureCrossSection(&theStep, mat)
02994                                   + MaterialSigmaF;
02995 
02996         G4double MaterialDensityG4 = mat->GetTotNbOfAtomsPerVolume()*cm3;
02997         G4double MaterialDensity = 0; // Atomic density
02998     for (size_t i=0; i<mat->GetNumberOfElements(); i++) {
02999 //             std::cout << (mat->GetDensity()/(g/cm3)) << " " << mat->GetFractionVector()[i] << " " << mat->GetElement(i)->GetA()/(g/mole) << " " << Avogadro << std::endl;
03000 //             std::cout << mat->GetTotNbOfAtomsPerVolume() << " " << mat->GetVecNbOfAtomsPerVolume()[i] << " " << mat->GetVecNbOfAtomsPerVolume()[i]/mat->GetTotNbOfAtomsPerVolume()*100 << std::endl;
03001             MaterialDensity += (mat->GetDensity()/(g/cm3)) * mat->GetFractionVector()[i] / (mat->GetElement(i)->GetA()/(g/mole)) * Avogadro;
03002         }
03003     G4double diff = 2*std::abs(MaterialDensityG4-MaterialDensity)/(MaterialDensityG4+MaterialDensity);
03004     if(diff >= 1e-7) std::cout << "EEEEEEEEK! " << MaterialDensityG4 << " ours " <<MaterialDensity <<std::endl;
03005 
03006 //         if(MaterialSigmaF >0) {
03007 //             std::cout << mat->GetName() << " sigmaf = " << MaterialSigmaF << " sigmaTot = " << MaterialSigmaTot << std::endl;
03008 //             std::cout << "density = " << MaterialDensity << " at/cm3 ; fLengthBetweenCollision = " << fLengthBetweenCollision << " cm ; nu = " << nu << std::endl;
03009 //             std::cout << MaterialDensity * fLengthBetweenCollision * nu * MaterialSigmaF << "\n" << std::endl;
03010 //         }
03011 
03012         // Collision keff
03013         if(MaterialSigmaTot > 0) {fEventAction->Add_keff_collision(nu * MaterialSigmaF / MaterialSigmaTot);}
03014 
03015         // Track length keff
03016 //         std::cout << mat->GetName() << " MaterialDensity : " << MaterialDensity << " fLengthBetweenCollision : " << fLengthBetweenCollision << " nu " << nu << " sigmaF : " << MaterialSigmaF << std::endl;
03017         fEventAction->Add_keff_trackLength(MaterialDensity * fLengthBetweenCollision * nu * MaterialSigmaF);
03018         fLengthBetweenCollision = 0;
03019     }
03020 
03021     if( processName == "nFission") {
03022         G4Material* mat = thePostPoint->GetPhysicalVolume()->GetLogicalVolume()->GetMaterial();
03023         G4VProcess* process = const_cast<G4VProcess*>(thePostPoint->GetProcessDefinedStep());
03024         G4HadronicProcess* hproc = dynamic_cast<G4HadronicProcess*>(process);
03025         const G4Isotope* target = hproc->GetTargetIsotope();
03026 
03027         const G4ElementTable *ElmTable = G4Element::GetElementTable();
03028         G4Element* elm = nullptr;
03029         for(size_t i=0; i<ElmTable->size(); ++i) {
03030             if((*ElmTable)[i]) {
03031                 if((*ElmTable)[i]->GetZ() == target->GetZ() && (*ElmTable)[i]->GetN() == target->GetN())
03032                     elm = (*ElmTable)[i];
03033             }
03034         }
03035 
03036         if(elm) { // if isotope has been found in the table of elements
03037             G4double ElementSigmaF = GetElementFissionCrossSection(&theStep, elm, mat);
03038             G4double ElementSigmaAbs = GetElementInelastCrossSection(&theStep, elm, mat)
03039                                     + GetElementCaptureCrossSection(&theStep, elm, mat);
03040             G4double ElementSigmaTot = GetElementElasticCrossSection(&theStep, elm, mat)
03041                                     + ElementSigmaAbs
03042                                     + ElementSigmaF;
03043 
03044 //         if(ElementSigmaF <0) {
03045 //             std::cout << mat->GetName() << " " << elm->GetName() << " sigmaf = " << ElementSigmaF << " sigmaTot = " << ElementSigmaTot << std::endl;
03046 //             std::cout << " ElementSigmaAbs = " << ElementSigmaAbs << " ; nu = " << nu << "\n" << std::endl;
03047 //             std::cout << nu*ElementSigmaF / (ElementSigmaAbs + ElementSigmaF) << "\n" << std::endl;
03048 //         }
03049 
03050             // Analog absorption keff
03051             // When a particle collides, there is a probability that it is absorbed by the nucleus.
03052             // In analog absorption, the particle is killed with that probability
03053             if( ElementSigmaAbs + ElementSigmaF ) { fEventAction->Add_keff_analogAbs(nu*ElementSigmaF / (ElementSigmaAbs + ElementSigmaF));}
03054 
03055             // Implicit absorption keff (not the case in GEANT4, 23/11/2020)
03056             // the particle is never killed by absorption;
03057             // instead, its weight is reduced by the absorption probability at eachcollision.
03058             if( ElementSigmaTot ) {fEventAction->Add_keff_implicitAbs(nu*ElementSigmaF / ElementSigmaTot);}
03059         }
03060     }
03061 //     std::cout << "End of MCNP_Keff_estimators" << std::endl;
03062 }
03063 
03064 void SteppingAction::MCNP_Keff_estimators(const G4Step &theStep) {
03065 
03066   // Get the post step point
03067   const G4StepPoint* thePostPoint = theStep.GetPostStepPoint();
03068   const G4StepPoint* thePrePoint  = theStep.GetPreStepPoint();
03069 
03070   // Get the process name
03071   G4String processName = "";
03072   if(thePostPoint->GetProcessDefinedStep()) {
03073       processName = thePostPoint->GetProcessDefinedStep()->GetProcessName();}
03074 
03075   // Exclude all particles but neutrons
03076   if(theStep.GetTrack()->GetDynamicParticle()->GetParticleDefinition()->GetParticleName() != "neutron") return;
03077 
03078   // Get the material
03079   G4Material* mat = thePostPoint->GetPhysicalVolume()->GetLogicalVolume()->GetMaterial();
03080   G4Material* matp = thePrePoint->GetPhysicalVolume()->GetLogicalVolume()->GetMaterial();
03081 
03082   G4double ken =theStep.GetPreStepPoint()->GetKineticEnergy();
03083 
03084   G4int nElements = mat->GetNumberOfElements();
03085   const G4double* nAtomsPerVolume = mat->GetVecNbOfAtomsPerVolume();
03086 //    G4double TotNbAtomsPerVolume = mat->GetTotNbOfAtomsPerVolume();
03087 
03088 G4int ich = 0;
03089 GetElementInelastChannelCrossSection(&theStep, (*mat->GetElementVector())[0], ich);
03090 
03091 
03092   G4double nufissCrossSection = 0;
03093   G4double captureCrossSection = 0;
03094   G4double totalCrossSection = 0;
03095   const G4DynamicParticle *dynp = theStep.GetTrack()->GetDynamicParticle();
03096   const G4DynamicParticle *pdynp = new G4DynamicParticle(dynp->GetParticleDefinition(),
03097                              dynp->GetMomentumDirection(),
03098                              ken);
03099   for(G4int i=0; i<nElements; ++i) {
03100     G4double fis = fProcessFis->GetCrossSectionDataStore()->GetCrossSection(pdynp, (*mat->GetElementVector())[i], mat);
03101     G4double cap = fProcessCap->GetCrossSectionDataStore()->GetCrossSection(pdynp, (*mat->GetElementVector())[i], mat);
03102     totalCrossSection += nAtomsPerVolume[i]*(
03103       fProcessElas->GetCrossSectionDataStore()->GetCrossSection(pdynp, (*mat->GetElementVector())[i], mat)+
03104       fProcessInel->GetCrossSectionDataStore()->GetCrossSection(pdynp, (*mat->GetElementVector())[i], mat)+
03105       cap+fis);
03106     nufissCrossSection +=  nAtomsPerVolume[i]*fEventAction->GetNu(ken)*fis;
03107     captureCrossSection +=  nAtomsPerVolume[i]*cap;
03108   }
03109 
03110   // Track length keff
03111   fEventAction->Add_keff_trackLength(theStep.GetStepLength()*nufissCrossSection);
03112   // Exclude non-collision processes and all particles but neutrons
03113   if(processName != "ParallelScoringWorld" && processName != "CoupledTransportation") {
03114     if(mat != matp && processName)
03115       std::cout << "matpre " << matp->GetName() << " matpost " << mat->GetName() << " proc "
03116         << processName << std::endl;
03117 
03118     if(processName != "hadElastic" &&
03119        processName != "neutronInelastic" &&
03120        processName != "nCapture" &&
03121        processName != "nFission") std::cout << " proc " << processName << std::endl;
03122 
03123   // Collision keff
03124     fEventAction->Add_keff_collision(nufissCrossSection / totalCrossSection);
03125   // Analog abs keff
03126     if(processName == "nCapture") fEventAction->Add_keff_analogAbs(nufissCrossSection / captureCrossSection);
03127   }
03128 
03129     /*
03130         // Track length keff
03131 //         std::cout << mat->GetName() << " MaterialDensity : " << MaterialDensity << " fLengthBetweenCollision : " << fLengthBetweenCollision << " nu " << nu << " sigmaF : " << MaterialSigmaF << std::endl;
03132         fEventAction->Add_keff_trackLength(MaterialDensity * fLengthBetweenCollision * nu * MaterialSigmaF);
03133         fLengthBetweenCollision = 0;
03134     }
03135 
03136     if( processName == "nFission") {
03137         G4Material* mat = thePostPoint->GetPhysicalVolume()->GetLogicalVolume()->GetMaterial();
03138         G4VProcess* process = const_cast<G4VProcess*>(thePostPoint->GetProcessDefinedStep());
03139         G4HadronicProcess* hproc = dynamic_cast<G4HadronicProcess*>(process);
03140         const G4Isotope* target = hproc->GetTargetIsotope();
03141 
03142         const G4ElementTable *ElmTable = G4Element::GetElementTable();
03143         G4Element* elm = nullptr;
03144         for(size_t i=0; i<ElmTable->size(); ++i) {
03145             if((*ElmTable)[i]) {
03146                 if((*ElmTable)[i]->GetZ() == target->GetZ() && (*ElmTable)[i]->GetN() == target->GetN())
03147                     elm = (*ElmTable)[i];
03148             }
03149         }
03150 
03151         if(elm) { // if isotope has been found in the table of elements
03152             G4double ElementSigmaF = GetElementFissionCrossSection(&theStep, elm, mat);
03153             G4double ElementSigmaAbs = GetElementInelastCrossSection(&theStep, elm, mat)
03154                                     + GetElementCaptureCrossSection(&theStep, elm, mat);
03155             G4double ElementSigmaTot = GetElementElasticCrossSection(&theStep, elm, mat)
03156                                     + ElementSigmaAbs
03157                                     + ElementSigmaF;
03158 
03159 //         if(ElementSigmaF <0) {
03160 //             std::cout << mat->GetName() << " " << elm->GetName() << " sigmaf = " << ElementSigmaF << " sigmaTot = " << ElementSigmaTot << std::endl;
03161 //             std::cout << " ElementSigmaAbs = " << ElementSigmaAbs << " ; nu = " << nu << "\n" << std::endl;
03162 //             std::cout << nu*ElementSigmaF / (ElementSigmaAbs + ElementSigmaF) << "\n" << std::endl;
03163 //         }
03164 
03165             // Analog absorption keff
03166             // When a particle collides, there is a probability that it is absorbed by the nucleus.
03167             // In analog absorption, the particle is killed with that probability
03168             if( ElementSigmaAbs + ElementSigmaF ) { fEventAction->Add_keff_analogAbs(nu*ElementSigmaF / (ElementSigmaAbs + ElementSigmaF));}
03169 
03170             // Implicit absorption keff (not the case in GEANT4, 23/11/2020)
03171             // the particle is never killed by absorption;
03172             // instead, its weight is reduced by the absorption probability at eachcollision.
03173             if( ElementSigmaTot ) {fEventAction->Add_keff_implicitAbs(nu*ElementSigmaF / ElementSigmaTot);}
03174         }
03175     }
03176 //     std::cout << "End of MCNP_Keff_estimators" << std::endl;
03177 */
03178 }
