00031 void TMXNeutronGenerationCounting::Initialize(EventAction* eventAction, TMXInputParameters* parameters) {
00032     fVect_GenerationAndStatus.clear();
00033     fIsNotSourceNeutron = false;
00034     fNeutronGeneration = 0;
00035     fTotalNeutronNumber=0;
00036     fEnergyAtBirth=0;
00037     fPosAtBirth = G4ThreeVector(0,0,0);
00038     fEventAction = eventAction;
00039     fInputParameters = parameters;
00040 }
00041 
00042 void TMXNeutronGenerationCounting::FillHistoSourceN(double energyAtBirth, G4ThreeVector posAtBirth, double energyAtCounting, G4ThreeVector posAtCounting, G4ThreeVector dirAtCounting, int nbNeutrons) {
00043     auto analysisManager = G4AnalysisManager::Instance();
00044     analysisManager->FillH1(16, log10(energyAtBirth), nbNeutrons);
00045     analysisManager->FillH1(17, log10(energyAtCounting), nbNeutrons);
00046     analysisManager->FillH2(32, posAtCounting.x()/mm, posAtCounting.y()/mm, nbNeutrons);
00047     analysisManager->FillH2(33, posAtCounting.x()/mm, posAtCounting.z()/mm, nbNeutrons);
00048     analysisManager->FillH2(34, posAtBirth.x()/mm, posAtBirth.y()/mm, nbNeutrons);
00049     analysisManager->FillH2(35, posAtBirth.x()/mm, posAtBirth.z()/mm, nbNeutrons);
00050     analysisManager->FillH2(36, dirAtCounting.unit().z(), log10(energyAtCounting), nbNeutrons);
00051     analysisManager->FillH2(37, std::acos(dirAtCounting.unit().z())* 360.0 / twopi, log10(energyAtCounting), nbNeutrons);
00052 }
00053 
00054 void TMXNeutronGenerationCounting::ScoreNeutronGeneration(long NeutronGeneration, int NeutronNumber) {
00055     fTotalNeutronNumber += NeutronNumber;
00056     fEventAction->SetNf(fTotalNeutronNumber);
00057     fEventAction->AddNbNeutronPerGeneration(NeutronGeneration, NeutronNumber);
00058     auto analysisManager = G4AnalysisManager::Instance();
00059     analysisManager->FillH1(15, NeutronGeneration, NeutronNumber); // +NeutronNumber neutron(s) from generation NeutronGeneration
00060 }
00061 
00062 void TMXNeutronGenerationCounting::InitializeGenerationFlag(const G4Step * theStep, bool isStepInTargetRegion) {
00063     if(theStep->GetTrack()
00064     && theStep->GetTrack()->GetDefinition()
00065     && theStep->GetTrack()->GetCurrentStepNumber() == 1) { // If first step of a track
00066         
00067         // if it is a primary particle generated by the primaryGenerator => generation = 0
00068         if(theStep->GetTrack()->GetParentID() == 0) {
00069             fNeutronGeneration = 0;
00070             fIsNotSourceNeutron = false;
00071             fVect_GenerationAndStatus.clear();
00072         }
00073         
00074         // The status and generation is retrieved from fVect_GenerationAndStatus; last element is deleted after reading
00075         if(fVect_GenerationAndStatus.size() > 0) {
00076             fNeutronGeneration = fVect_GenerationAndStatus.back().first;
00077             fIsNotSourceNeutron = fVect_GenerationAndStatus.back().second;
00078             fVect_GenerationAndStatus.pop_back();
00079         }
00080         // If the track is not a primary
00081         else if (theStep->GetTrack()->GetParentID() != 0) {
00082             // The vector is empty only for primaries
00083             std::cout << "ERROR in reading of fVect_GenerationAndStatus. The vector size is " << fVect_GenerationAndStatus.size() << std::endl;
00084         }
00085         
00086         // if it is a source neutron, get energy and position at birth
00087         if(theStep->GetTrack()->GetDefinition()->GetParticleName() == "neutron"
00088             && fNeutronGeneration == 0
00089         ) {
00090             fEnergyAtBirth = theStep->GetPreStepPoint()->GetKineticEnergy()/eV;
00091             fPosAtBirth    = theStep->GetPreStepPoint()->GetPosition();
00092         }
00093     }
00094     
00095     // If the conditions of source neutron definition are not fulfilled the default condition fIsNotSourceNeutron=false must be changed to true
00096     ChangeStatusFlag(theStep, isStepInTargetRegion);
00097 }
00098 
00100 //
00101 // This method is used to change fIsNotSourceNeutron and start the counting of neutron generations
00102 //
00103 void TMXNeutronGenerationCounting::ChangeStatusFlag(const G4Step * theStep, bool isStepInTargetRegion) {
00104     // If the status is still false
00105     if( !fIsNotSourceNeutron &&
00106             // and the particle is outside the target (e.g. in the beam window) and the definition of source neutron is not the Energy Amplifier's definition
00107         ( (!TMXOption::IsSourceNeutronEADefinition(fInputParameters->int_Option) && !isStepInTargetRegion)
00108            // or old definition of source neutrons (Energy Amplifier 1995): with neutron counted as source neutron when E < 20 MeV and the particle is a neutron
00109         || (TMXOption::IsSourceNeutronEADefinition(fInputParameters->int_Option) && theStep->GetTrack()->GetDefinition()->GetParticleName() == "neutron" && theStep->GetPreStepPoint()->GetKineticEnergy() < 20)
00110           // or we have an external source file
00111         || !fInputParameters->ExtSourcePath.empty()
00112     )) {
00113         fIsNotSourceNeutron = true;
00114         fNeutronGeneration = 0;    // When the flag is changed to false, the counting of neutron generation starts (from 0).
00115         
00116         // If the track is neutron
00117         if(theStep->GetTrack()
00118             && theStep->GetTrack()->GetDefinition()
00119             && theStep->GetTrack()->GetDefinition()->GetParticleName() == "neutron") {
00120         
00121             // Score neutron birth for neutron counting
00122             // Track quantities
00123             G4double energyK = theStep->GetPreStepPoint()->GetKineticEnergy()/eV;
00124 
00125             // PreStep quantities
00126             G4ThreeVector PrePos   = theStep->GetPreStepPoint()->GetPosition();
00127             G4ThreeVector PreMom   = theStep->GetPreStepPoint()->GetMomentumDirection();
00128 
00129             // Scoring  
00130             ScoreNeutronGeneration(0, 1);
00131             FillHistoSourceN(fEnergyAtBirth, fPosAtBirth, energyK, PrePos, PreMom, 1);
00132             FillNtupleNeutronSource(PrePos, PreMom, energyK);
00133         }
00134     }
00135 }
00136 
00138 //
00139 // This method is used to count neutron per generations
00140 //
00141 void TMXNeutronGenerationCounting::NeutronGenerationCounting(const G4Step* theStep, const G4Track* secondary, 
00142                                                 int nNeutrons, int& Nb_inelastic) {
00143     
00144     G4String processName = "";
00145     if(theStep->GetPostStepPoint()->GetProcessDefinedStep()) {
00146         processName = theStep->GetPostStepPoint()->GetProcessDefinedStep()->GetProcessName();}
00147         
00148     // if the status is at true, the conditions of source neutron definition are not fulfilled anymore and the counting can start
00149     if(fIsNotSourceNeutron) {
00150 
00151         if (processName == "nFission") { // If particles are generated by fission
00152             // The secondary generation is +1
00153             fVect_GenerationAndStatus.push_back( {fNeutronGeneration+1, fIsNotSourceNeutron});
00154             
00155             // If the secondary is a neutron, the neutron generation is scored
00156             if(secondary->GetDefinition()->GetParticleName() == "neutron") {
00157                 ScoreNeutronGeneration(fNeutronGeneration+1, 1);
00158             }
00159         }
00160         else if(processName == "neutronInelastic") { // If secondary neutrons are generated by inelastic reaction
00161             // Count number of secondary neutrons from inelastic reaction
00162             if(secondary->GetDefinition()->GetParticleName() == "neutron")
00163                 ++Nb_inelastic;
00164             
00165             // If (n, n' + whatever except neutrons) reaction or neutron absorption,
00166             if(nNeutrons <= 1){
00167                 // the neutron generation remains the same and it is not scored as a new neutron
00168                 fVect_GenerationAndStatus.push_back( {fNeutronGeneration, fIsNotSourceNeutron});
00169             }
00170             // If source neutrons definition 1 : (n,Xn) does not change the secondary neutron generation,
00171             else if( TMXOption::IsSrcNeutronDefinitionWithNxN(fInputParameters->int_Option) /*|| nNeutrons == 1*/) {
00172                 // the neutron generation remains the same and it scored as a new neutron
00173                 fVect_GenerationAndStatus.push_back( {fNeutronGeneration, fIsNotSourceNeutron});  
00174                 
00175                 // If the secondary is a neutron and already 1 secondary neutron has been read, the neutron generation is scored
00176                 if(secondary->GetDefinition()->GetParticleName() == "neutron" && Nb_inelastic > 1) {
00177                     ScoreNeutronGeneration(fNeutronGeneration, 1);
00178                     
00179                     if(fNeutronGeneration == 0) {
00180                         FillHistoSourceN(secondary->GetKineticEnergy()/eV,  // At birth
00181                                          secondary->GetPosition(),          // At birth
00182                                          secondary->GetKineticEnergy()/eV,      // At counting
00183                                          secondary->GetPosition(),              // At counting
00184                                          secondary->GetMomentumDirection(), 1); // At counting
00185                         FillNtupleNeutronSource(secondary->GetPosition(), 
00186                                                 secondary->GetMomentumDirection(), 
00187                                                 secondary->GetKineticEnergy()/eV);
00188                     }
00189                 }
00190             }
00191             else { // else it is (n, Xn + whatever except neutron) reaction, the secondary neutron generation is +1
00192                 fVect_GenerationAndStatus.push_back( {fNeutronGeneration+1, fIsNotSourceNeutron}); 
00193                 
00194                 // If the secondary is a neutron, the neutron generation is scored
00195                 if(secondary->GetDefinition()->GetParticleName() == "neutron") {
00196                     ScoreNeutronGeneration(fNeutronGeneration+1, 1);
00197                 }
00198             }
00199         }
00200         else { // Else the process is not a nfission or neutronInelastic
00201             
00202             // The generation and status is not changed
00203             fVect_GenerationAndStatus.push_back( {fNeutronGeneration, fIsNotSourceNeutron} );
00204             
00205             // If the secondary is a neutron and not his mother, the neutron generation is scored
00206             if(theStep->GetTrack()->GetDefinition()->GetParticleName() != "neutron"
00207                 && secondary->GetDefinition()->GetParticleName() == "neutron"
00208             ) {
00209                 ScoreNeutronGeneration(fNeutronGeneration, 1);
00210                 if(fNeutronGeneration == 0) {
00211                     FillHistoSourceN(secondary->GetKineticEnergy()/eV,  // At birth
00212                                      secondary->GetPosition(),          // At birth
00213                                      secondary->GetKineticEnergy()/eV,      // At counting
00214                                      secondary->GetPosition(),              // At counting
00215                                      secondary->GetMomentumDirection(), 1); // At counting
00216                     FillNtupleNeutronSource(secondary->GetPosition(), 
00217                                             secondary->GetMomentumDirection(), 
00218                                             secondary->GetKineticEnergy()/eV);
00219                 }
00220             }
00221         }
00222     }
00223     else { // else the status is still at false, the conditions of source neutron definition are still fulfilled
00224         // Every secondaries inherits of the status and generation of its mother
00225         fVect_GenerationAndStatus.push_back( {fNeutronGeneration, fIsNotSourceNeutron} );
00226     }    
00227 }
00228 
00229 void TMXNeutronGenerationCounting::FillNtupleNeutronSource(G4ThreeVector N0_Pos, G4ThreeVector N0_Momentum, G4double N0_Energy) {
00230     if (N0_Energy > 0.) {
00231         G4int row = 3;
00232         auto analysisManager = G4AnalysisManager::Instance();
00233         analysisManager->FillNtupleFColumn(row, 0, N0_Pos.x()/mm);         // ID= 0 X Position
00234         analysisManager->FillNtupleFColumn(row, 1, N0_Pos.y()/mm);         // ID= 1 Y Position
00235         analysisManager->FillNtupleFColumn(row, 2, N0_Pos.z()/mm);         // ID= 2 Z Position
00236         analysisManager->FillNtupleFColumn(row, 3, N0_Momentum.x());    // ID= 3 X Momentum
00237         analysisManager->FillNtupleFColumn(row, 4, N0_Momentum.y());    // ID= 4 Y Momentum
00238         analysisManager->FillNtupleFColumn(row, 5, N0_Momentum.z());    // ID= 5 Z Momentum
00239         analysisManager->FillNtupleDColumn(row, 6, N0_Energy);       // ID= 6   Energy
00240         analysisManager->AddNtupleRow(row);
00241     }
00242 }
