00125 void TMXkeff::computeKeffCycle()
00126 {
00127     m_fissionSourceBank.nextCycle();
00128     m_nu.push_back(m_fissionSourceBank.getNumberOfFissionSites() / m_nbFission);
00129     int nxnAdd = 0;
00130 
00131     m_kgenPerCycle.push_back( m_nbFissionPerCycle[m_currentCycle]         / ((double)m_nbParticlePerCycle + (double)nxnAdd));
00132     m_kcolPerCycle.push_back( m_nu[m_currentCycle - 1] * m_nbCollisionsPerCycle[m_currentCycle] / ((double)m_nbParticlePerCycle + (double)nxnAdd));
00133     m_ktrkPerCycle.push_back( m_nu[m_currentCycle - 1] * m_nbTrckLengthPerCycle[m_currentCycle] / ((double)m_nbParticlePerCycle + (double)nxnAdd));
00134     m_kabsPerCycle.push_back( m_nu[m_currentCycle - 1] * m_nbAbsorptionPerCycle[m_currentCycle] / ((double)m_nbParticlePerCycle + (double)nxnAdd));
00135 
00136     m_energyFromFission.push_back( m_eperfiss / m_countFission.back());
00137     m_vecEPerFission.push_back(m_eperfiss);
00138 
00139     printCycleKeff();
00140     if (m_currentCycle == m_nbTotCycle) printAll();
00141     nextCycle();
00142 }
