00151 G4double TMXkeff::GetMaterialXS(const G4Step * theStep, std::string XSreaction)
00152 {
00153     const G4DynamicParticle* particleDyn = theStep->GetTrack()->GetDynamicParticle();
00154     particleDyn                          = new G4DynamicParticle(particleDyn->GetParticleDefinition(),
00155                                                                  particleDyn->GetMomentumDirection(),
00156                                                                  theStep->GetPreStepPoint()->GetKineticEnergy());
00157     G4Material *mat                      = theStep->GetPreStepPoint()->GetMaterial();
00158     G4ParticleDefinition *particle       = G4ParticleTable::GetParticleTable()->FindParticle("neutron");
00159     G4HadronicProcess * process          = (G4HadronicProcess *)particle->GetProcessManager()->GetProcess(XSreaction);
00160     G4double section = process->GetCrossSectionDataStore()->GetCrossSection(particleDyn, mat) * cm;
00161     delete particleDyn;
00162     return section;
00163 }
