00171 void TMXkeff::inGeneratePrimaries(G4ParticleGun * particleGun, G4Event * anEvent)
00172 {
00173     // Add Wielandt acceleration
00174     if (m_currentCycle <= 1)
00175     {
00176         G4ThreeVector originPosition;
00177         if (!m_originPositions.empty()) {
00178             originPosition = m_originPositions[rand() % m_originPositions.size()];
00179         }
00180         else {
00181             originPosition = G4ThreeVector();
00182         }
00183         particleGun->SetParticlePosition(originPosition);
00184         // particleGun->SetParticleEnergy(14. * MeV);
00185         particleGun->SetParticleEnergy(m_sampler->G4SampleWatt(92235, G4FFGEnumerations::NEUTRON_INDUCED, 0.025*eV));  // THIS HAS TO BE TESTED AGAINST THE 14 MeV NEUTRONS AND VALIDATED
00186     }
00187     else
00188     {
00189         G4ThreeVector pos = m_fissionSourceBank.getFissionSiteLoc(G4UniformRand());
00190         G4double ener     = m_fissionSourceBank.getFissionSiteE(G4UniformRand());
00191         particleGun->SetParticlePosition(pos);
00192         particleGun->SetParticleEnergy(ener);
00193         if(m_samplingSourceNumber == m_nbParticlePerCycle || m_samplingSourceNumber == getNumberOfFissionSites() - 1)
00194             m_samplingSourceNumber = 0;
00195         else {m_samplingSourceNumber++;}
00196     }
00197     G4double cosTheta = 2. * G4UniformRand() - 1.;
00198     G4double phi      = twopi * G4UniformRand();
00199     G4double sinTheta = std::sqrt(1. - cosTheta * cosTheta);
00200     G4double ux       = sinTheta * std::cos(phi);
00201     G4double uy       = sinTheta * std::sin(phi);
00202     G4double uz       = cosTheta;
00203     particleGun->SetParticleMomentumDirection(G4ThreeVector(ux, uy, uz));
00204     particleGun->GeneratePrimaryVertex(anEvent);
00205 
00206 }
