00288 void TMXkeff::inUserSteppingAction(const G4Step * theStep)
00289 {
00290 
00291     if(theStep->GetTrack()->GetParticleDefinition() == G4Neutron::NeutronDefinition())
00292     {
00293         addTrackLength(GetMaterialXS(theStep, "nFission") * (theStep->GetStepLength() / cm));
00294     }
00295 
00296 
00297     if(theStep->GetPostStepPoint()->GetPhysicalVolume()
00298     && theStep->GetTrack()->GetParticleDefinition() == G4Neutron::NeutronDefinition() // changed on 14/04/2021: Addition of this condition for keff collision calculation that was not protected by the process name
00299 // 1st modif: no effect on keff -> checked
00300     )
00301     {
00302         G4AutoLock lock(&myHEPPrimGenMutex);
00303         G4Track* theTrack                  = theStep->GetTrack();
00304 
00305         G4StepPoint* thePostPoint          = theStep->GetPostStepPoint();
00306         G4String processName               = thePostPoint->GetProcessDefinedStep()->GetProcessName();
00307         G4ParticleDefinition* particleType = theTrack->GetDefinition();
00308         G4String particleName              = particleType->GetParticleName();
00309 
