00311         G4double MaterialSimaCap           = GetMaterialXS(theStep, "nCapture");
00312         G4double MaterialSigmaAbs          = MaterialSigmaF
00313                                            + MaterialSimaCap;
00314         G4double MaterialSigmaTot          = GetMaterialXS(theStep, "hadElastic")
00315                                            + GetMaterialXS(theStep, "neutronInelastic")
00316                                            + MaterialSigmaAbs;
00317 
00318         if(processName == "nCapture" ) m_countCapture[m_currentCycle - 1]++;
00319 
00320         if(processName == "nFission" || processName == "nCapture")
00321         {
00322             addAbsorption(MaterialSigmaF/MaterialSigmaAbs);
00323         }
00324 
00325         if ( theStep->GetPostStepPoint()->GetStepStatus() != fGeomBoundary && processName != "ParallelScoringWorld" && processName != "ParallelScoringWorld_polar")
00326         {
00327             addCollision( MaterialSigmaF / MaterialSigmaTot);
00328         }
00329 
00330         if(processName == "nFission")
00331         {
00332 //            std::cout<<"NOT " << theStep->GetTrack()->GetParticleDefinition()->GetParticleName()<<" "
