00339             for(G4int i=0; i<(G4int)secondaries->size(); ++i)
00340             {
00341                 G4String secondary_particleName = (*secondaries)[i]->GetDefinition()->GetParticleName();
00342                 if (secondary_particleName == "neutron")
00343                 {
00344                         countSecNeutrons++;
00345                         addFissionSite(thePostPoint->GetPosition(), (*secondaries)[i]->GetMomentumDirection(), (*secondaries)[i]->GetKineticEnergy(), (*secondaries)[i]->GetMomentum());
00346                         m_fissionSourceBank.addEnergySite( (*secondaries)[i]->GetKineticEnergy());
00347                 }
00348                 m_eperfiss += (*secondaries)[i]->GetKineticEnergy() / MeV;
00349            }
00350             m_fissionEnergy[m_currentCycle - 1] += theStep->GetPreStepPoint()->GetKineticEnergy() / MeV;
