00360 
00365 double TMXkeff::computeMeanStdev(std::vector<double> k)
00366 {
00367     double sum         = 0.;
00368     double sumSquare   = 0.;
00369     if ((int)k.size() <= m_nbCycleToDiscard + 1) return 0.;
00370     for (std::vector<double>::size_type i = m_nbCycleToDiscard; i < k.size(); i++)
00371     {
00372         sumSquare     += k[i] * k[i];
00373         sum           += k[i];
00374     }
00375     double mean        = sum / (k.size() - m_nbCycleToDiscard);
00376     double mean2       = sumSquare / (k.size() - m_nbCycleToDiscard);
00377     return std::sqrt((mean2 - mean * mean) / (k.size() - m_nbCycleToDiscard - 1));
00378 }
00379 
00380 
00386 double TMXkeff::computeMean(std::vector<double> k)
00387 {
00388     double average = 0.;
00389     if ((int)k.size() <= m_nbCycleToDiscard) return 0.;
00390     for (std::vector<double>::size_type i = m_nbCycleToDiscard; i < k.size(); i++)
00391         average += k[i];
00392     return average / (k.size() - m_nbCycleToDiscard);
00393 }
