00399 void TMXkeff::nextCycle()
00400 {
00401     m_countFission.push_back(0);
00402     m_countCapture.push_back(0);
00403     m_fissionEnergy.push_back(0.);
00404 
00405     m_nbFissionPerCycle.push_back(0);
00406     m_nbCollisionsPerCycle.push_back(0);
00407     m_nbTrckLengthPerCycle.push_back(0);
00408     m_nbAbsorptionPerCycle.push_back(0);
00409     m_nbFission = 0;
00410     m_eperfiss = 0.;
00411     m_samplingSourceNumber = 0;
00412 
00413     m_currentCycle++;
00414     std::cout<<scientific<<std::endl;
00415 
00416 }
