00513 void TMXkeff::setNbParticlePerCycle(int nbParticlePerCycle)
00514 {
00515     m_nbFissionPerCycle.push_back(nbParticlePerCycle);
00516     m_nbCollisionsPerCycle.push_back(nbParticlePerCycle);
00517     m_nbTrckLengthPerCycle.push_back(nbParticlePerCycle);
00518     m_nbAbsorptionPerCycle.push_back(nbParticlePerCycle);
00519     m_nbParticlePerCycle = nbParticlePerCycle;
00520     nextCycle();
00521 }
