00591 void TMXkeff::printCycleKeff()
00592 {
00593     writeKResults();
00594 
00595         std::cout <<                                                                                                                          std::endl;
00596         std::cout << "**************************************************************"                                                      << std::endl;
00597         std::cout << "*********************  KEFF_CALCULATION  *********************"                                                      << std::endl;
00598         std::cout << "**************************************************************"                                                      << std::endl;
00599     if (m_currentCycle > m_nbCycleToDiscard )
00600     {
00601         std::cout << "Cycle: "        << m_currentCycle                                                                                    << std::endl;
00602         std::cout << " -- "                                                                                                                << std::endl;
00603         std::cout << fixed << setprecision(6);
00604         std::cout << "Kgen-Cycle: "   << m_kgenPerCycle[m_currentCycle - 1]                                                                << std::endl;
00605         std::cout << "Kcol-Cycle: "   << m_kcolPerCycle[m_currentCycle - 1]                                                                << std::endl;
00606         std::cout << "Ktrk-Cycle: "   << m_ktrkPerCycle[m_currentCycle - 1]                                                                << std::endl;
00607         std::cout << "Kabs-Cycle: "   << m_kabsPerCycle[m_currentCycle - 1]                                                                << std::endl;
00608         std::cout << " -- "                                                                                                                << std::endl;
00609         std::cout << "Kgen: "         << computeMean(m_kgenPerCycle)
00610                   << " Uncertainty: "  << computeMeanStdev(m_kgenPerCycle)                                                                 << std::endl;
00611         std::cout << "kcol: "        << computeMean(m_kcolPerCycle)
00612                   << " Uncertainty: "  << computeMeanStdev(m_kcolPerCycle)                                                                 << std::endl;
00613         std::cout << "Ktrk: "         << computeMean(m_ktrkPerCycle)
00614                   << " Uncertainty: "  << computeMeanStdev(m_ktrkPerCycle)                                                                 << std::endl;
00615         std::cout << "Kabs: "         << computeMean(m_kabsPerCycle)
00616                   << " Uncertainty: "  << computeMeanStdev(m_kabsPerCycle)                                                                 << std::endl;
00617         std::cout << "--------------------------------------------------------------"                                                      << std::endl;
00618         std::cout << fixed << setprecision(6);
00619         std::cout << "NuFiss   : " << (double)m_countFission[m_currentCycle - 1] / (double)m_nbParticlePerCycle * m_nu[m_currentCycle - 1] << std::endl;
00620         std::cout << "Nu       : " << m_nu[m_currentCycle - 1]                                                                             << std::endl;
00621         std::cout << "Ener@Fiss: " << (double)m_fissionEnergy[m_currentCycle - 1] / (double)m_countFission[m_currentCycle - 1]             << std::endl;
00622         std::cout << "EnPerFiss: " << (double)m_vecEPerFission[m_currentCycle - 1] / (double)m_countFission[m_currentCycle - 1]             << std::endl;
00623         std::cout << "Fissions : " << (double)m_countFission[m_currentCycle - 1] / (double)m_nbParticlePerCycle                            << std::endl;
00624         std::cout << "Captures : " << (double)m_countCapture[m_currentCycle - 1] / (double)m_nbParticlePerCycle                            << std::endl;
00625         std::cout << " -- "                                                                                                                << std::endl;
00626         std::cout << fixed << setprecision(6);
00627         std::cout << "NuFiss   : " << computeMean(m_nu) * computeMean(m_countFission) / (double)m_nbParticlePerCycle
00628                   << " Uncertainty: "  << computeMeanStdev(multiplyVectors(m_nu, m_countFission)) / (double)m_nbParticlePerCycle           << std::endl;
00629         std::cout << "Nu       : " << computeMean(m_nu)
00630                   << " Uncertainty: "  << computeMeanStdev(m_nu)                                                                           << std::endl;
00631         std::cout << "Ener@Fiss: " << computeMean(m_fissionEnergy) / computeMean(m_countFission)
00632                   << " Uncertainty: "  << computeMeanStdev(divideVectors(m_fissionEnergy, m_countFission))                                 << std::endl;
00633         std::cout << "EnPerFiss: " << computeMean(m_vecEPerFission) / computeMean(m_countFission)
00634                   << " Uncertainty: "  << computeMeanStdev(divideVectors(m_vecEPerFission, m_countFission))                                 << std::endl;
00635         std::cout << "Fissions : " << computeMean(m_countFission) / (double)m_nbParticlePerCycle
00636                   << " Uncertainty: "  << computeMeanStdev(m_countFission) / (double)m_nbParticlePerCycle                                  << std::endl;
00637         std::cout << "Captures : " << computeMean(m_countCapture) / (double)m_nbParticlePerCycle
00638                   << " Uncertainty: "  << computeMeanStdev(m_countCapture) / (double)m_nbParticlePerCycle                                  << std::endl;
00639     }
00640     else
00641     {
00642         std::cout << fixed << setprecision(6);
00643         std::cout << "Cycle: "     << m_currentCycle                                                                                       << std::endl;
00644         std::cout << "Kgen-Cycle: "<< m_kgenPerCycle[m_currentCycle - 1]                                                                   << std::endl;
00645         std::cout << "Kcol-Cycle: "<< m_kcolPerCycle[m_currentCycle - 1]                                                                   << std::endl;
00646         std::cout << "Ktrk-Cycle: "<< m_ktrkPerCycle[m_currentCycle - 1]                                                                   << std::endl;
00647         std::cout << "Kabs-Cycle: "<< m_kabsPerCycle[m_currentCycle - 1]                                                                   << std::endl;
00648         std::cout << "**************************************************************"                                                      << std::endl;
00649         std::cout << fixed         << setprecision(6);
00650         std::cout << "NuFiss   : " << (double)m_countFission[m_currentCycle - 1] / (double)m_nbParticlePerCycle * m_nu[m_currentCycle - 1] << std::endl;
00651         std::cout << "Nu       : " << (double)m_nu[m_currentCycle - 1]                                                                     << std::endl;
00652         std::cout << "Ener@Fiss: " << (double)m_fissionEnergy[m_currentCycle - 1] / (double)m_countFission[m_currentCycle - 1]             << std::endl;
00653         std::cout << "EnPerFiss: " << (double)m_vecEPerFission[m_currentCycle - 1] / (double)m_countFission[m_currentCycle - 1]             << std::endl;
00654         std::cout << "Fissions : " << (double)m_countFission[m_currentCycle - 1] / (double)m_nbParticlePerCycle                            << std::endl;
00655         std::cout << "Captures : " << (double)m_countCapture[m_currentCycle - 1] / (double)m_nbParticlePerCycle                            << std::endl;
00656     }
00657         std::cout << "**************************************************************"                                                      << std::endl;
00658         std::cout <<                                                                                                                          std::endl;
00659 }
00660 
00661 std::vector<double> TMXkeff::multiplyVectors(std::vector<double> V1, std::vector<double> V2)
00662 {
00663     std::vector<double> v;
00664     for(unsigned i = 0; i < V1.size(); i++)
00665     {
00666         v.push_back(V1[i] * V2[i]);
00667     }
00668     return v;
00669 }
00670 
00671 std::vector<double> TMXkeff::divideVectors(std::vector<double> V1, std::vector<double> V2)
00672 {
00673     std::vector<double> v;
00674     for(unsigned i = 0; i < V1.size(); i++)
00675     {
00676         v.push_back(V1[i] / V2[i]);
00677     }
00678     return v;
00679 }
