00684 void TMXkeff::printAll()
00685 {
00686     cout.precision(5);
00687     std::cout << "-------------------------KEFF VALUES------------------------"   << std::endl;
00688     std::cout << std::left << std::setw(12) << "Cycle"    << std::setw(12) << "kgen"
00689                                                           << std::setw(12) << "kcol"
00690                                                           << std::setw(12) << "ktrk"
00691                                                           << std::setw(12) << "kabs" << std::endl;
00692     for (std::vector<double>::size_type i = 0; i < m_kcolPerCycle.size(); i++)
00693     {
00694         if((int)i == m_nbCycleToDiscard)
00695             std::cout << "================== ACTIVE CYCLES ==================" <<std::endl;
00696         std::cout << fixed << setprecision(6) << std::left << std::setw(12) << i + 1  << std::setw(12) << m_kgenPerCycle[i]
00697                                                            << std::setw(12) << m_kcolPerCycle[i]
00698                                                            << std::setw(12) << m_ktrkPerCycle[i]
00699                                                            << std::setw(12) << m_kabsPerCycle[i] << std::endl;
00700     }
00701     std::cout << "------------------------------------------------------------" << std::endl;
00702     std::cout << "Calculation performed for " << m_nbTotCycle << " cycles of "
00703               << m_nbFissionPerCycle[0] << " particles discarding " << m_nbCycleToDiscard << " cycles" << std::endl;
00704     std::cout << "------------------------------------------------------------" << std::endl;
00705     std::cout << fixed << setprecision(6) << std::left << std::setw(12) << "kgen: "   << std::setw(12) << computeMean(m_kgenPerCycle)
00706                                                           << " ( " << computeMeanStdev(m_kgenPerCycle) << " ) " << std::endl;
00707     std::cout << fixed << setprecision(6) << std::left << std::setw(12) << "kcol: "   << std::setw(12) << computeMean(m_kcolPerCycle)
00708                                                           << " ( " << computeMeanStdev(m_kcolPerCycle) << " ) " << std::endl;
00709     std::cout << fixed << setprecision(6) << std::left << std::setw(12) << "ktrk: "   << std::setw(12) << computeMean(m_ktrkPerCycle)
00710                                                           << " ( " << computeMeanStdev(m_ktrkPerCycle) << " ) " << std::endl;
00711     std::cout << fixed << setprecision(6) << std::left << std::setw(12) << "kabs: "   << std::setw(12) << computeMean(m_kabsPerCycle)
00712                                                           << " ( " << computeMeanStdev(m_kabsPerCycle) << " ) " << std::endl;
00713     std::cout << "------------------------------------------------------------" << std::endl;
00714 }
