00720 void TMXkeff::writeKResults()
00721 {
00722     if(m_currentCycle == 1)
00723     {
00724         m_File.open(m_kFilename, std::ios::out);
00725 
00726 //        m_File.setf( std::ios::dec, std::ios::floatfield );
00727 //        m_File.precision(5);
00728 
00729         m_File << "**************************************************************" << std::endl;
00730         m_File << "*********************  KEFF_CALCULATION  *********************" << std::endl;
00731         m_File << "**************************************************************" << std::endl;
00732         m_File <<                                                                     std::endl;
00733         m_File << "------------------------------------------------------------"   << std::endl;
00734         m_File << "Calculation performed for " << m_nbTotCycle
00735                << " cycles of "                << m_nbFissionPerCycle[0]
00736                << " particles discarding "     << m_nbCycleToDiscard << " cycles"  << std::endl;
00737         m_File <<                                                                     std::endl;
00738         m_File << std::left << std::setw(12) << "Cycle"  << std::setw(12) << "kgenCycle"
00739                                                          << std::setw(12) << "kcolCycle"
00740                                                          << std::setw(12) << "kabsCycle"
00741                                                          << std::setw(12) << "ktrkCycle"
00742                                                          << std::setw(12) << "kgen"
00743                                                          << std::setw(12) << "Uncert."
00744                                                          << std::setw(12) << "kcol"
00745                                                          << std::setw(12) << "Uncert."
00746                                                          << std::setw(12) << "kabs"
00747                                                          << std::setw(12) << "Uncert."
00748                                                          << std::setw(12) << "ktrk"
00749                                                          << std::setw(12) << "Uncert."
00750                                                                                    << std::endl;
00751         m_File.close();
00752     }
00753     m_File.open(m_kFilename, ios::app);
00754 //    m_File.setf( std::ios::dec, std::ios::floatfield );
00755 //    m_File.precision(5);
00756 
00757     if(m_currentCycle - 1 == m_nbCycleToDiscard)
00758         m_File << "================== ACTIVE CYCLES ==================" <<std::endl;
00759     m_File << std::left << std::setw(12) << m_currentCycle
00760                      << fixed << setprecision(6) << std::setw(12) << m_kgenPerCycle[m_currentCycle - 1]
00761                                                  << std::setw(12) << m_kcolPerCycle[m_currentCycle - 1]
00762                                                  << std::setw(12) << m_kabsPerCycle[m_currentCycle - 1]
00763                                                  << std::setw(12) << m_ktrkPerCycle[m_currentCycle - 1]
00764                                                  << std::setw(12) << computeMean(m_kgenPerCycle)
00765                                                  << std::setw(12) << computeMeanStdev(m_kgenPerCycle)
00766                                                  << std::setw(12) << computeMean(m_kcolPerCycle)
00767                                                  << std::setw(12) << computeMeanStdev(m_kcolPerCycle)
00768                                                  << std::setw(12) << computeMean(m_kabsPerCycle)
00769                                                  << std::setw(12) << computeMeanStdev(m_kabsPerCycle)
00770                                                  << std::setw(12) << computeMean(m_ktrkPerCycle)
00771                                                  << std::setw(12) << computeMeanStdev(m_ktrkPerCycle) << std::endl;
00772     if(m_currentCycle == m_nbTotCycle)
00773     {
00774 
00775         m_File << "------------------------------------------------------------" << std::endl;
00776         m_File << fixed << setprecision(6) << std::left << std::setw(12) << "kgen: "   << std::setw(12) << computeMean(m_kgenPerCycle)
00777                                                               << " ( " << computeMeanStdev(m_kgenPerCycle) << " ) " << std::endl;
00778         m_File << fixed << setprecision(6) << std::left << std::setw(12) << "kcol: "   << std::setw(12) << computeMean(m_kcolPerCycle)
00779                                                               << " ( " << computeMeanStdev(m_kcolPerCycle) << " ) " << std::endl;
00780         m_File << fixed << setprecision(6) << std::left << std::setw(12) << "kabs: "   << std::setw(12) << computeMean(m_kabsPerCycle)
00781                                                               << " ( " << computeMeanStdev(m_kabsPerCycle) << " ) " << std::endl;
00782         m_File << fixed << setprecision(6) << std::left << std::setw(12) << "ktrk: "   << std::setw(12) << computeMean(m_ktrkPerCycle)
