00029 SVBR100_FuelAssembly::SVBR100_FuelAssembly(std::string name, TMXInputParameters* p)
00030 :   TMXAbstractFuelAssembly(name, p),
00031     m_name(name),
00032     m_p{p},
00033     m_overlap(m_p->Verbose >= 1),
00034     m_index(""),
00035     m_masterVolume(nullptr),
00036     m_translationVector(G4ThreeVector()),
00037     m_primaryCoolantMaterial(nullptr),
00038     m_metalicStructureMaterial(nullptr),
00039     m_fuelPin(nullptr),
00040     m_nbFuelPinSections(1),
