00054 void SVBR100_FuelAssembly::buildFuelPinLattice(G4ThreeVector translation) {
00055     m_assemblyLattice = new TMXLattice();
00056     m_assemblyLattice->setLatticeMap(m_p->PathToAssemblyLatticeFile);
00057     // m_assemblyLattice->setPitch(15. * mm); // Optionnal, can be set with the Map file
00058     m_assemblyLattice->setMotherVolume(m_masterVolume);
00059 
00060     m_fuelPin = new SVBR100_FuelPin("SVBR100_FuelPin_"+m_index, m_p);
00061     m_fuelPin->setHalfHeight(m_halfHeight_fuelPin);
00062     m_fuelPin->setNbSections(m_nbFuelPinSections);
00063     m_fuelPin->setFuelAssemblyIndex(m_index);
00064     m_fuelPin->createGeometry();
00065     m_assemblyLattice->setElementTranslation(translation);
00066     m_assemblyLattice->addDaughterVolume("F", m_fuelPin->getMasterVolume());
00067 
00068     m_assemblyLattice->placeLattice(m_overlap);
00069 }
00070 
00071 void SVBR100_FuelAssembly::createGeometry() {
00072     createMaterial();
00073 
00074     // Grid
00075     G4double halfHeight_upperGrid = 10./2. * mm;
00076     G4double halfHeight_intermediateGrid = 40./2. * mm;
00077     G4double halfHeight_lowerGrid = 8./2. * mm;
00078 
00079     // Pin
00080     m_halfHeight_fuelPin = 1550./2. * mm;
00081     G4double halfHeight_upperVoidFuelPin = 30./2. * mm;
00082 
00083     G4double halfHeight_assembly = m_halfHeight_fuelPin + halfHeight_upperVoidFuelPin + 110. * mm; // = 900 mm // 1845./2. * mm;
00084     G4double outerRadius_assembly = 225.45/2. * mm;
00085 
00086     G4double zPosition_fuelPin = -halfHeight_assembly + 2.*halfHeight_lowerGrid + 2.*halfHeight_intermediateGrid + m_halfHeight_fuelPin;
00087 
00088     // Control Rod
00089     G4double halfHeight_controlRod = 900./2. * mm;
00090     m_zOffset_controlRod = zPosition_fuelPin + (900.+150.-65.)/2. * mm + halfHeight_controlRod;
00091 
00092     // Fuel Assembly Inner Structure
00093     G4double innerRadius_innerStructure = 60./2. * mm;
00094     G4double thickness_innerStructure = 1. * mm;
00095     G4double outerRadius_innerStructure = innerRadius_innerStructure + thickness_innerStructure;
00096 
00097     // std::string m_index = m_index != -1 ? "_Round"+std::to_string(m_index) : "";
00098 
00099     //--------------------------------------------------------------------------*
00101     // Fuel Assembly Container (Master Volume)
00102     G4Polyhedra* sMasterVolume = new G4Polyhedra(
00103         "s"+m_name+"_container_"+m_index,    // name
00104         0.,                 // phiStart
00105         360. * deg,         // phiTotal
00106         6,                  // numSide
00107         2,                  // numZPlanes
00108         new double [2]{-halfHeight_assembly, halfHeight_assembly + 2.*halfHeight_controlRod},      // zPlane[]
00109         new double [2]{0., 0.}, // rInner[]
00110         new double [2]{outerRadius_assembly, outerRadius_assembly}  // rOuter[]
00111     );
00112 
00113     m_masterVolume = new G4LogicalVolume(sMasterVolume, m_primaryCoolantMaterial, "l"+m_name+"_container_"+m_index);
00114 
00116     // Fuel Assembly Inner Structure
00117     G4Polyhedra* sInnerStructure = new G4Polyhedra(
00118         "s"+m_name+"_innerStructure_"+m_index,    // name
00119         0.,                 // phiStart
00120         360. * deg,         // phiTotal
00121         6,                  // numSide
00122         2,                  // numZPlanes
00123         new double [2]{-halfHeight_assembly, halfHeight_assembly},      // zPlane[]
00124         new double [2]{innerRadius_innerStructure, innerRadius_innerStructure}, // rInner[]
00125         new double [2]{outerRadius_innerStructure, outerRadius_innerStructure}  // rOuter[]
00126     );
00127 
00128     G4LogicalVolume* lInnerStructure = new G4LogicalVolume(sInnerStructure, m_metalicStructureMaterial, "l"+m_name+"_innerStructure_"+m_index);
00129     new G4PVPlacement(0, G4ThreeVector(), lInnerStructure, "p"+m_name+"_innerStructure_"+m_index, m_masterVolume, false, 0, m_overlap);
00130 
00132     // Upper Grid
00133     G4double zPosition_upperGrid = -halfHeight_assembly + 2.*(halfHeight_lowerGrid + halfHeight_intermediateGrid + m_halfHeight_fuelPin + halfHeight_upperVoidFuelPin) + halfHeight_upperGrid; // halfHeight_fuelPin + halfHeight_upperGrid + halfHeight_upperVoidFuelPin;
00134 
00135     G4Polyhedra* sUpperGrid = new G4Polyhedra(
00136         "s"+m_name+"_upperGrid_"+m_index,    // name
00137         0.,                 // phiStart
00138         360. * deg,         // phiTotal
00139         6,                  // numSide
00140         2,                  // numZPlanes
00141         new double [2]{-halfHeight_upperGrid, halfHeight_upperGrid},      // zPlane[]
00142         new double [2]{outerRadius_innerStructure, outerRadius_innerStructure}, // rInner[]
00143         new double [2]{outerRadius_assembly, outerRadius_assembly}  // rOuter[]
00144     );
00145 
00146     G4LogicalVolume* lUpperGrid = new G4LogicalVolume(sUpperGrid, m_metalicStructureMaterial, "l"+m_name+"_upperGrid_"+m_index);
00147     new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_upperGrid), lUpperGrid, "p"+m_name+"_upperGrid_"+m_index, m_masterVolume, false, 0, m_overlap);
00148 
00150     // Intermediate Grid
00151     G4double zPosition_intermediateGrid = -halfHeight_assembly + 2.*halfHeight_lowerGrid + halfHeight_intermediateGrid; // -m_halfHeight_fuelPin - halfHeight_intermediateGrid + 0.*halfHeight_upperVoidFuelPin;
00152 
00153     G4Polyhedra* sIntermediateGrid = new G4Polyhedra(
00154         "s"+m_name+"_intermediateGrid_"+m_index,    // name
00155         0.,                 // phiStart
00156         360. * deg,         // phiTotal
00157         6,                  // numSide
00158         2,                  // numZPlanes
00159         new double [2]{-halfHeight_intermediateGrid, halfHeight_intermediateGrid},      // zPlane[]
00160         new double [2]{outerRadius_innerStructure, outerRadius_innerStructure}, // rInner[]
00161         new double [2]{outerRadius_assembly, outerRadius_assembly}  // rOuter[]
00162     );
00163 
00164     G4LogicalVolume* lIntermediateGrid = new G4LogicalVolume(sIntermediateGrid, m_metalicStructureMaterial, "l"+m_name+"_intermediateGrid_"+m_index);
00165     new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_intermediateGrid), lIntermediateGrid, "p"+m_name+"_intermediateGrid_"+m_index, m_masterVolume, false, 0, m_overlap);
00166 
00168     // Lower Grid
00169     G4double zPosition_lowerGrid = -halfHeight_assembly + halfHeight_lowerGrid;// -m_halfHeight_fuelPin - 2.*halfHeight_intermediateGrid - halfHeight_lowerGrid + 0.*halfHeight_upperVoidFuelPin;
00170 
00171     G4Polyhedra* sLowerGrid = new G4Polyhedra(
00172         "s"+m_name+"_lowerGrid_"+m_index,    // name
00173         0.,                 // phiStart
00174         360. * deg,         // phiTotal
00175         6,                  // numSide
00176         2,                  // numZPlanes
00177         new double [2]{-halfHeight_lowerGrid, halfHeight_lowerGrid},      // zPlane[]
00178         new double [2]{outerRadius_innerStructure, outerRadius_innerStructure}, // rInner[]
00179         new double [2]{outerRadius_assembly, outerRadius_assembly}  // rOuter[]
00180     );
00181 
00182     G4LogicalVolume* lLowerGrid = new G4LogicalVolume(sLowerGrid, m_metalicStructureMaterial, "l"+m_name+"_lowerGrid_"+m_index);
00183     new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_lowerGrid), lLowerGrid, "p"+m_name+"_lowerGrid_"+m_index, m_masterVolume, false, 0, m_overlap);
00184 
00186     // Lattice of Fuel Pins
00187     buildFuelPinLattice(G4ThreeVector(0., 0., zPosition_fuelPin));
00188 
00190     // Control Rod
00191     G4Tubs* sControlRod = new G4Tubs("s"+m_name+"_controlRod_"+m_index, 0., innerRadius_innerStructure, halfHeight_controlRod, 0., twopi);
00192     m_lControlRod = new G4LogicalVolume(sControlRod, m_absorptionMaterial, "l"+m_name+"_controlRod_"+m_index);
00193 }
00194 
00195 void SVBR100_FuelAssembly::createMaterial() {
00197     // Boron Carbide
00198     const double fractionB10 = 0.199;
00199     const double scale = 1000.;
00200     m_absorptionMaterial = new TMXMaterial("B4C_"+m_name, 1.1*(g/cm3), 3, kStateSolid, (0.5 * (485.+340.) + 273.15) * kelvin);
00201     m_absorptionMaterial->AddElement( 50100, (int)(scale * 4. *       fractionB10 )/*500*/); // B-10
00202     m_absorptionMaterial->AddElement( 50110, (int)(scale * 4. * (1. - fractionB10))/*500*/); // B-11
00203     m_absorptionMaterial->AddElement( 60120, (int)(scale * 1.)                     /*250*/); // C-12
00204 }
