00218 
00219 void SVBR100_FuelAssembly::placeControlRod(double zRodInsertion) {
00220     if (!m_isControlRodPlaced) {
00221         setZRodInsertion(zRodInsertion*mm);
00222 
00223         // std::string m_index = m_index != -1 ? "_Round"+std::to_string(m_index) : "";
00224         new G4PVPlacement(0, G4ThreeVector(0., 0., m_zOffset_controlRod - zRodInsertion*mm), m_lControlRod, "pFuelAssembly_controlRod_"+m_index, m_masterVolume, false, 0, m_overlap);
00225 
00226         m_isControlRodPlaced = true;
00227     }
00228 }
