00034 TMXTarget::TMXTarget(std::string name, TMXInputParameters* p, G4LogicalVolume* motherVolume)
00035 :   // TMXAbstractTargetAssembly(p, name),
00036     m_name(name),
00037     m_p{p},
00038     m_overlap(m_p->Verbose >= 1),
00039     m_motherVolume(motherVolume),
00040     m_lBeamEnvelope(nullptr),
00041     m_translationVector(G4ThreeVector()),
00042     m_offset_target(0.),
00043     m_outerRadius_upperBeamEnvelope(0.)
00044 {
00045 
00046 }
