00051 void TMXTarget::createGeometry(G4double extraLength_lower, G4double extraLength_upper) {
00052     createMaterial();
00053 
00054     //--------------------------------------------------------------------------*
00055     // Configuration Selection
00056     bool isOriginal     = false;
00057     bool isLightVersion = false;
00058     bool isIteration2   = false;
00059     bool isIteration3   = false; // Abort due to hydraudynalical perturbations
00060     // 2021-07-07 - After Hydraulic Simulations
00061     bool isC_I          = false;
00062     bool isC_II         = true;
00063 
00064     bool isFlowGuidesInW = true;
00065     //--------------------------------------------------------------------------*
00066 
00067     G4double thickness_wall = 5. * mm;
00068 
00069     // Beam Envelope
00070     G4double halfLength_supportBeamEnvelope = 45./2. * mm; 
00071     G4double halfLength_upperBeamEnvelope = 2000./2. * mm + extraLength_upper/2.; // 1993.95/2. * mm;
00072     G4double halfLength_junction = 55./2. * mm; // 55.45/2. * mm;
00073     G4double halfLength_lowerBeamEnvelope = 995./2. * mm + extraLength_lower/2.; // 995.82/2. * mm;
00074     G4double halfLength_beamEnvelope = halfLength_upperBeamEnvelope + halfLength_junction + halfLength_lowerBeamEnvelope; // 3050. * mm;
00075 
00076     G4double outerRadius_supportBeamEnvelope = 455./2. * mm; // 1000./2. * mm;
00077            m_outerRadius_upperBeamEnvelope = 455./2. * mm;
00078     G4double outerRadius_lowerBeamEnvelope = 210./2. * mm;
00079 
00080     G4double distance_beamWindow_bottomBeamEnvelope = 625. * mm;
00081     G4double length_innerShell = 675 * mm + extraLength_lower;
00082     m_offset_target = halfLength_lowerBeamEnvelope - distance_beamWindow_bottomBeamEnvelope; // center on the tip of the beam window
00083 
00084     // Beam Tube
00085     G4double outerRadius_beamTube = 250./2. * mm;
00086     G4double halfLength_beamTube = (2.*halfLength_beamEnvelope - distance_beamWindow_bottomBeamEnvelope - length_innerShell)/2.;
00087     G4double zPosition_beamTube = -halfLength_lowerBeamEnvelope + distance_beamWindow_bottomBeamEnvelope + halfLength_beamTube + length_innerShell;
00088 
00089     // Upper Coolant
00090     G4double innerRadius_upperCoolant = outerRadius_beamTube + thickness_wall;
00091     G4double outerRadius_upperCoolant = m_outerRadius_upperBeamEnvelope - thickness_wall;
00092     G4double halfLength_upperCoolant = (2.*halfLength_beamEnvelope - distance_beamWindow_bottomBeamEnvelope - length_innerShell)/2. - halfLength_supportBeamEnvelope;
00093     G4double zPosition_upperCoolant = zPosition_beamTube - halfLength_beamTube + halfLength_upperCoolant;
00094 
00095     // Intermediate Void Envelope
00096     G4double outerRadius_intermediateVoidEnvelope = 192.5 * mm;
00097     G4double innerRadius_intermediateVoidEnvelope = 155. * mm;
00098     G4double halfLength_intermediateVoidEnvelope = 1471./2. * mm + extraLength_upper/2.;
00099     G4double zPosition_intermediateVoidEnvelope = -halfLength_upperCoolant + halfLength_intermediateVoidEnvelope;
00100 
00101     // Intermediate Void
00102     G4double outerRadius_intermediateVoid = outerRadius_intermediateVoidEnvelope - thickness_wall;
00103     G4double innerRadius_intermediateVoid = innerRadius_intermediateVoidEnvelope + thickness_wall;
00104     G4double halfLength_intermediateVoid = halfLength_intermediateVoidEnvelope - thickness_wall/2.;
00105     G4double zPosition_intermediateVoid = -halfLength_intermediateVoidEnvelope + halfLength_intermediateVoid;
00106 
00107     // EM Pomp
00108     G4double outerRadius_outerEMPomp = outerRadius_intermediateVoid;
00109     G4double innerRadius_outerEMPomp = innerRadius_intermediateVoid;
00110     G4double halfLength_outerEMPomp = 400./2. * mm;
00111 
00112     G4double outerRadius_innerEMPomp = outerRadius_beamTube;
00113     G4double innerRadius_innerEMPomp = 174./2. * mm;
00114     G4double halfLength_innerEMPomp = halfLength_outerEMPomp;
00115 
00116     G4double distance_bottomEMP_bottomBeamEnvelope = 2150. * mm;
00117     G4double zPosition_innerEMPomp = -halfLength_beamTube - length_innerShell - distance_beamWindow_bottomBeamEnvelope + distance_bottomEMP_bottomBeamEnvelope + halfLength_innerEMPomp + extraLength_upper + extraLength_lower;
00118     G4double zPosition_outerEMPomp = -halfLength_intermediateVoid - length_innerShell - distance_beamWindow_bottomBeamEnvelope + distance_bottomEMP_bottomBeamEnvelope + halfLength_outerEMPomp + extraLength_upper + extraLength_lower;
00119 
00120     // Heat Exchanger
00121     G4double thickness_heatExchangerWing = 11.5 * mm;
00122     G4double innerRadius_HXWing = outerRadius_intermediateVoidEnvelope;
00123     G4double outerRadius_HXWing = outerRadius_upperCoolant;
00124     G4double halfLength_HXWing = halfLength_intermediateVoidEnvelope;
00125     G4double zPosition_HXWings = zPosition_intermediateVoidEnvelope;
00126 
00127     if (m_p->Verbose >= 2) {
00128         G4cout << "Length Upper    Beam Envelope: " << halfLength_upperBeamEnvelope * 2. << " mm" << G4endl
00129                << "Length Junction Beam Envelope: " << halfLength_junction * 2. << " mm" << G4endl
00130                << "Length Lower    Beam Envelope: " << halfLength_lowerBeamEnvelope * 2. << " mm" << G4endl
00131                << "Diameter Upper Beam Envelope: " << m_outerRadius_upperBeamEnvelope * 2. << " mm" << G4endl
00132                << "Diameter Lower Beam Envelope: " << outerRadius_lowerBeamEnvelope * 2. << " mm" << G4endl
00133                << "Offset Target" << m_offset_target << " mm" << G4endl;
00134     }
00135 
