00140     // Beam Envelope
00141     G4Tubs* sSupportBeamEnvelope = new G4Tubs("sSupportBeamEnvelope", 0., outerRadius_supportBeamEnvelope, halfLength_supportBeamEnvelope, 0., twopi);
00142     G4Tubs* sUpperBeamEnvelope = new G4Tubs("sUpperBeamEnvelope", 0., m_outerRadius_upperBeamEnvelope, halfLength_upperBeamEnvelope, 0., twopi);
00143     G4Cons* sJunctionBeamEnvelope = new G4Cons("sJunctionBeamEnvelope",
00144             0.*(outerRadius_lowerBeamEnvelope - thickness_wall), // pRmin1
00145             outerRadius_lowerBeamEnvelope, // pRmax1
00146             0.*(m_outerRadius_upperBeamEnvelope - 30.*mm), // pRmin2
00147             m_outerRadius_upperBeamEnvelope, // pRmax2
00148             halfLength_junction, // pDz
00149             0., // pSPhi
00150             twopi); // pDPhi
00151     // Lower Cylinder == Center of the Target
00152     G4Tubs* sLowerBeamEnvelope = new G4Tubs("sLowerBeamEnvelope", 0., outerRadius_lowerBeamEnvelope, halfLength_lowerBeamEnvelope, 0., twopi);
00153 
00154     G4VSolid* sBeamEnvelopeCylinder = new G4UnionSolid("sBeamEnvelopeCylinder", sLowerBeamEnvelope, sUpperBeamEnvelope, 0, G4ThreeVector(0., 0., halfLength_upperBeamEnvelope+halfLength_lowerBeamEnvelope+2.*halfLength_junction));
00155     G4VSolid* sBeamEnvelopePencil = new G4UnionSolid("sBeamEnvelopePencil", sBeamEnvelopeCylinder, sJunctionBeamEnvelope, 0, G4ThreeVector(0., 0., halfLength_lowerBeamEnvelope+halfLength_junction));
00156     G4VSolid* sBeamEnvelope = new G4UnionSolid("sBeamEnvelope", sBeamEnvelopePencil, sSupportBeamEnvelope, 0, G4ThreeVector(0., 0., 2.*halfLength_upperBeamEnvelope+halfLength_lowerBeamEnvelope+2.*halfLength_junction-halfLength_supportBeamEnvelope));
00157 
00158     m_lBeamEnvelope = new G4LogicalVolume(sBeamEnvelope, m_envelopeMaterial, "lBeamEnvelope");
00159 
00161     // Beam Tube
00162     G4Tubs* sBeamTube = new G4Tubs("sBeamTube", 0., outerRadius_beamTube, halfLength_beamTube, 0., twopi);
00163     G4LogicalVolume* lBeamTube = new G4LogicalVolume(sBeamTube, m_beamVoidMaterial, "lBeamTube");
00164     new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_beamTube), lBeamTube, "pBeamTube", m_lBeamEnvelope, false, 0, m_overlap);
00165 
00167     // Upper Coolant
00168     G4Tubs* sUpperCoolant = new G4Tubs("sUpperCoolant", innerRadius_upperCoolant, outerRadius_upperCoolant, halfLength_upperCoolant, 0., twopi);
00169     G4LogicalVolume* lUpperCoolant = new G4LogicalVolume(sUpperCoolant, m_leadCoolantMaterial, "lUpperCoolant");
00170     new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_upperCoolant), lUpperCoolant, "pUpperCoolant", m_lBeamEnvelope, false, 0, m_overlap);
