00187     G4LogicalVolume* lInnerEMPomp = new G4LogicalVolume(sInnerEMPomp, m_copper, "lTarget_innerEMPomp");
00188     new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_innerEMPomp), lInnerEMPomp, "pTarget_innerEMPomp", lBeamTube, false, 0, m_overlap);
