00195     // Heat Exchanger Wings
00196     int nWing = 100;
00197     G4Tubs* sWingSector = new G4Tubs("sWingSector", innerRadius_HXWing, outerRadius_HXWing, halfLength_HXWing, -twopi/(double)nWing * 0.5, twopi/(double)nWing);
00198     G4LogicalVolume* lWingSector = new G4LogicalVolume(sWingSector, m_leadCoolantMaterial, "lWingSector");
00199 
00200     G4Tubs* sHXContainer = new G4Tubs("sHXContainer", innerRadius_HXWing, outerRadius_HXWing, halfLength_HXWing, 0., twopi);
00201     G4LogicalVolume* lHXContainer = new G4LogicalVolume(sHXContainer, m_leadCoolantMaterial, "lHXContainer");
00202 
00203     new G4PVReplica("pHXWings_replica", lWingSector, lHXContainer, kPhi, nWing, twopi/(double)nWing);
00204     new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_HXWings), lHXContainer, "pHXWings", lUpperCoolant, false, 0, m_overlap);
