00206     G4Trd* sWing = new G4Trd("sWing",
00207         halfLength_HXWing, // dx1
00208         halfLength_HXWing, // dx2
00209         3.*mm, // dy1
00210         1.*mm, // dy2
00211         thickness_heatExchangerWing/2.// dz
00212     );
00213     G4LogicalVolume* lWing = new G4LogicalVolume(sWing, m_envelopeMaterial, "lWing");
00214     G4RotationMatrix* yRot = new G4RotationMatrix; // Rotates on Y axes only
00215     yRot->rotateY(-90.*deg);
00216     new G4PVPlacement(yRot, G4ThreeVector(innerRadius_HXWing+thickness_heatExchangerWing/2., 0., 0.), lWing, "pWing", lWingSector, false, 0, m_overlap);
