00511         const double outerRadius_stretchFactor = 0.9;
00512         const G4double zOffset_vane = 5.5 * mm;
00513         const G4double length_vane = 10. * mm; // Projected length of a vane along z-axis
00514         G4double innerRadius_vane = 5. * mm;
00515         G4double outerRadius_vane = 10. * mm;
00516         const G4double thickness_vane = 1. * mm;
00517         std::string ext;
00518         for (int i = 0; i < nVanes; i++) {
00519             ext = std::to_string(i);
00520             G4GenericPolycone* sVanes = new G4GenericPolycone("sVanes_"+ext, 0., twopi, nSegmentVane,
00521                 new double[nSegmentVane]{
00522                     innerRadius_vane*(innerRadius_stretchFactor*i+1),
00523                     outerRadius_vane*(outerRadius_stretchFactor*i+1),
00524                     outerRadius_vane*(outerRadius_stretchFactor*i+1) + thickness_vane,
00525                     innerRadius_vane*(innerRadius_stretchFactor*i+1) + thickness_vane
00526                 },
00527                 new double[nSegmentVane]{
00528                     zOffset_vane + z2 + i*distanceBetweenVanes,
00529                     zOffset_vane + length_vane + z2 + i*distanceBetweenVanes,
00530                     zOffset_vane + length_vane + z2 + i*distanceBetweenVanes,
00531                     zOffset_vane + z2 + i*distanceBetweenVanes
00532                 }
00533             );
00534             G4LogicalVolume* lVanes = new G4LogicalVolume(sVanes, m_envelopeMaterial, "lVanes_"+ext);
00535             new G4PVPlacement(0, G4ThreeVector(), lVanes, "pVanes_"+ext, lInner_outerShell, false, 0, m_overlap);
00536         }
00537     }
00538 }
00539 
00540 void TMXTarget::createMaterial() {
00541     const G4double temperature_PrimaryCoolant = (0.5 * (485.+340.) + 273.15) * kelvin;
00542 
00544     // Target
00545     m_targetMaterial = new TMXMaterial(
00546         "LBESpallationMaterial_"+m_name, 10.23*(g/cm3), 5, kStateSolid, temperature_PrimaryCoolant
00547     );
00548     // Pb Nat - 4 Stable Isotopes - 44.5%
00549     m_targetMaterial->AddElement(822040, 0.445 * 0.014); // Pb204
00550     m_targetMaterial->AddElement(822060, 0.445 * 0.241); // Pb206
00551     m_targetMaterial->AddElement(822070, 0.445 * 0.221); // Pb207
00552     m_targetMaterial->AddElement(822080, 0.445 * 0.524); // Pb208
00553     // Bi Nat - + Stable Isotope - 55.5%
00554     m_targetMaterial->AddElement(832090, 0.555); // Bi209
00555 
00557     // Lead Coolant
00558     m_leadCoolantMaterial = new TMXMaterial(
00559         "LBECoolantMaterial_"+m_name, 10.23*(g/cm3), 5, kStateSolid, temperature_PrimaryCoolant
00560     );
00561     // Pb Nat - 4 Stable Isotopes - 44.5%
00562     m_leadCoolantMaterial->AddElement(822040, 0.445 * 0.014); // Pb204
00563     m_leadCoolantMaterial->AddElement(822060, 0.445 * 0.241); // Pb206
00564     m_leadCoolantMaterial->AddElement(822070, 0.445 * 0.221); // Pb207
00565     m_leadCoolantMaterial->AddElement(822080, 0.445 * 0.524); // Pb208
00566     // Bi Nat - + Stable Isotope - 55.5%
00567     m_leadCoolantMaterial->AddElement(832090, 0.555); // Bi209
00568 
00570     // Beam Void
00571     m_beamVoidMaterial = new TMXMaterial(
00572         "BeamVoidMaterial_"+m_name, 0.000001 * 10.23*(g/cm3), 5, kStateGas, temperature_PrimaryCoolant
00573     );
00574     // Pb Nat - 4 Stable Isotopes - 44.5%
00575     m_beamVoidMaterial->AddElement(822040, 0.445 * 0.014); // Pb204
00576     m_beamVoidMaterial->AddElement(822060, 0.445 * 0.241); // Pb206
00577     m_beamVoidMaterial->AddElement(822070, 0.445 * 0.221); // Pb207
00578     m_beamVoidMaterial->AddElement(822080, 0.445 * 0.524); // Pb208
00579     // Bi Nat - + Stable Isotope - 55.5%
00580     m_beamVoidMaterial->AddElement(832090, 0.555); // Bi209
00581 
00583     // Flow Guide
00584     m_flowGuideMaterial = new TMXMaterial(
00585         "FlowGuideMaterial_"+m_name, 19.3*(g/cm3), 4, kStateSolid, temperature_PrimaryCoolant
00586     );
00587     m_flowGuideMaterial->AddElement(741820, 0.2650); // W182
00588     m_flowGuideMaterial->AddElement(741830, 0.1431); // W183
