00696         double frac_Mo = 1.03e-2; // wt%
00697         HT9->AddElement(420920, frac_Mo * 0.1465); // Mo92
00698         HT9->AddElement(420940, frac_Mo * 0.0919); // Mo94
00699         HT9->AddElement(420950, frac_Mo * 0.1587); // Mo95
00700         HT9->AddElement(420960, frac_Mo * 0.1667); // Mo96
00701         HT9->AddElement(420970, frac_Mo * 0.0958); // Mo97
00702         HT9->AddElement(420980, frac_Mo * 0.2429); // Mo98
00703         HT9->AddElement(421000, frac_Mo * 0.0974); // Mo100
00704         // Ni Nat - 5 Stable Isotopes
00705         double frac_Ni = 0.51e-2; // wt%
00706         HT9->AddElement(280580, frac_Ni * 0.68077); // Ni58
00707         HT9->AddElement(280600, frac_Ni * 0.26223); // Ni60
00708         HT9->AddElement(280610, frac_Ni * 0.01140); // Ni61
00709         HT9->AddElement(280620, frac_Ni * 0.03635); // Ni62
00710         HT9->AddElement(280640, frac_Ni * 0.00926); // Ni64
00711         // Mn Nat - 1 Stable Isotope
00712         double frac_Mn = 0.5e-2; // wt%
00713         HT9->AddElement(250550, frac_Mn); // Mn55
00714         // W Nat - 5 Stable Isotopes
00715         double frac_W = 0.50e-2; // wt%
00716         HT9->AddElement(741800, frac_W * 0.0012); // W-180
00717         HT9->AddElement(741820, frac_W * 0.2650); // W-182
00718         HT9->AddElement(741830, frac_W * 0.1431); // W-183
00719         HT9->AddElement(741840, frac_W * 0.3064); // W-184
00720         HT9->AddElement(741860, frac_W * 0.2843); // W-186
00721         // V Nat - 2 Stable Isotopes
00722         double frac_V = 0.33e-2; // wt%
00723         HT9->AddElement(230500, frac_V * 0.0025); // V-50
00724         HT9->AddElement(230510, frac_V * 0.9975); // V-51
00725         // Si Nat - 3 Stable Isotopes
00726         double frac_Si = 0.21e-2; // wt%
00727         HT9->AddElement(140280, frac_Si * 0.922); // Si28
00728         HT9->AddElement(140290, frac_Si * 0.047); // Si29
00729         HT9->AddElement(140300, frac_Si * 0.031); // Si30
00730         // C Nat - 2 Stable Isotopes
00731         double frac_C = 0.21e-2; // wt%
00732         HT9->AddElement( 60120, frac_C * 0.989); // C-12
00733         HT9->AddElement( 60120, frac_C * 0.011); // C-13
00734         // N Nat - 2 Stable Isotopes
00735         double frac_N = 0.008e-2; // wt%
00736         HT9->AddElement( 70140, frac_N * 0.996); // N-14
00737         HT9->AddElement( 70150, frac_N * 0.004); // N-15
00738         // S Nat - 4 Stable Isotopes
00739         double frac_S = 0.003e-2; // wt%
00740         HT9->AddElement(160320, frac_S * 0.9499); // S-32
00741         HT9->AddElement(160330, frac_S * 0.0075); // S-33
00742         HT9->AddElement(160340, frac_S * 0.0425); // S-34
00743         HT9->AddElement(160360, frac_S * 0.0001); // S-36
00744         // Fe Nat - 4 Stable Isotopes
00745         double frac_Fe = 1. - (
00746             frac_C+frac_Mn+frac_S+frac_Si+frac_Cr+frac_Ni+frac_Mo+frac_V+frac_W
00747         ); // wt%
00748         HT9->AddElement(260540, frac_Fe * 0.05845); // Fe54
00749         HT9->AddElement(260560, frac_Fe * 0.91754); // Fe56
00750         HT9->AddElement(260570, frac_Fe * 0.02119); // Fe57
00751         HT9->AddElement(260580, frac_Fe * 0.00282); // Fe58
00752     }
00753 }
