00756 // Placer
00757 void TMXTarget::placeGeometry(G4ThreeVector translation) {
00758     m_translationVector = translation;
00759     new G4PVPlacement(0, G4ThreeVector(0., 0., m_offset_target) + m_translationVector, m_lBeamEnvelope, "pBeamPipe_"+m_name, m_motherVolume, false, 0, m_overlap);
00760 }
00761 
00762 void TMXTarget::placeGeometry() {
00763     placeGeometry(m_translationVector);
00764 }
