00061 void DefineMaterials2011::Initialize(TMXInputParameters* parameter) {
00062     fparameter = parameter;
00063     
00065     // TMX Materials
00066     
00067     // World materials
00068     fUniverseMaterial = nullptr;
00069     fAirMaterial = nullptr;
00070     fVacuum = nullptr;
00071     fConcreteMaterial = nullptr;
00072     fWorldLeadMaterial = nullptr;
00073     fWorldWaterMaterial = nullptr;
00074     fWorldAluMaterial = nullptr;
00075     fWorldBiMaterial = nullptr;
00076     fWorldAlF3Material = nullptr;
00077     
00078     // Core materials
00079     fCladAluMaterial = nullptr;
00080     fAlAlloyMaterial = nullptr;
00081     fFuelWaterMaterial = nullptr;
00082     fFuelAbsorMaterial = nullptr;
00083     fReactorMaterial = nullptr;
00084     fLeadRigMaterial = nullptr;
00085     fCladSsMaterial = nullptr;
00086     fFuelCladAluMaterial = nullptr;
00087     fFuelCladAluTempFuelMaterial = nullptr;
00088     fFuelReflMaterial4.clear();
00089     
00090     // Nuclear fuel
00091     fUO2AlMaterial = nullptr;
00092     fThO2_Material = nullptr;
00093     fRecycledPu_Material = nullptr;
00094     fFreshMOX_Material = nullptr;
00095     fAm241_Material = nullptr;
00096     fAm242_Material = nullptr;
00097     fAm243_Material = nullptr;
00098     fPu238_Material = nullptr;
00099     fPu241_Material = nullptr;
00100     fMixAm_Material = nullptr;
00101     fMixCm_Material = nullptr;
00102     fDirtPu_Material = nullptr;
00103     fThPuO2_Material = nullptr;
00104     fFuelBurnupMWd_kgU.clear();
00105     
00106     // c ========  Definition of cell in LVR-15 model  =================
00107     // c
00108     // c FA temperature                  - 376 K    = 3.239992e-8 MeV
00109     // c FA surface temperature          - 353 K    = 8.617e-11*353 = 3.041801e-8 MeV
00110     // c Water temperature inside FA     - 318 K    = 2.740206e-8 MeV
00111     // c Water temperature inside LVR-15 - 293 K    = 2.524781e-8 MeV
00112 
00113     ftemperature_fuel       = 293*kelvin; // UO2 temperature
00114     ftemperature_FuelSurf   = 293*kelvin; // Fuel Cladding
00115     ftemperature_PrimaryCoolantInCore  = 293*kelvin; // Temperature of water insidde FA + temperature non-FA elements in core
00116     ftemperature_PrimaryCoolantOutOfCore = 293*kelvin; // Temperature of water inside LVR-15 reactor
00117     ftemperature_PrimaryCoolantAndReflectorAndCR = 293*kelvin; // Temperature of water inside fuel assembly, control rods, beryllium rods
00118 }
00119 
00123 // Read MCNP material card file from CVR to build GEANT4 material with same fuel compositions
00124 //
00128 std::vector< std::vector <TMXMaterial*>>* DefineMaterials2011::GetFuelBurnupMWd_kgU(double burnup_MWd_kgU) {
00129     std::vector<std::pair<int, G4double> > V_nAtoms; // vector of atomic fractions
00130     bool bool_atomic_fraction = false;
00131   
00132 //     std::string strFileName = "../../LVR15fuel_with_burnup";
00133     std::string strFileName = fparameter->PathToBurnupFile;
00134     std::ifstream myfile;
00135     myfile.open (strFileName, std::ifstream::binary);
00136     std::string strLine;
00137 
00138     // Looking for the position of the closest burnup in the data file
00139     std::size_t Burnup_Pos=0;
00140     double min_difference = burnup_MWd_kgU + 10;
00141     std::string Infile_burnup = "";
00142     bool new_format = false;
00143 
00144     while(std::getline (myfile,strLine)) {
00145         std::size_t Pos = strLine.find("Burnup", 0);
00146         std::size_t file_pos = myfile.tellg();
00147         if(Pos != std::string::npos) {
00148 //             std::cout << strLine.substr(Pos+7, strLine.size() -15 - Pos) << std::endl;
00149 //             std::cout << "min_difference : " << min_difference << std::endl;
00150 //             std::cout << "difference : " << std::abs(burnup_MWd_kgU - std::stod(strLine.substr(Pos+7, strLine.size() -15 - Pos))) << std::endl;
00151             if( std::abs(burnup_MWd_kgU - std::stod(strLine.substr(Pos+7, strLine.size() -15 - Pos))) <= min_difference) {
00152                 min_difference = std::abs(burnup_MWd_kgU - std::stod(strLine.substr(Pos+7, strLine.size() -15 - Pos)));
00153                 Burnup_Pos = file_pos;   // Position in the file of the closest burnup to burnup_MWd_kgU
00154                 Infile_burnup = strLine.substr(Pos+7, strLine.size() -15 - Pos);
00155                 new_format = false;
00156             }
00157         }
00158         else if(strLine.find("vyhoreni", 0) != std::string::npos) { // Implementation of a new format of MCNP material file; "vyhoreni" means "burnup" in Czech
00159             std::string mat_name;
00160             std::getline (myfile,mat_name); // Get the following line to read the material name since each vertical slice has its own burnup
00161             if( mat_name.at(5) == '3' ) { // First vertical slice
00162                 Pos = strLine.find("vyhoreni", 0);
00163                 if( std::abs(burnup_MWd_kgU - std::stod(strLine.substr(Pos+8, strLine.size() -15 - Pos-8))) <= min_difference) {
00164                     min_difference = std::abs(burnup_MWd_kgU - std::stod(strLine.substr(Pos+8, strLine.size() -15 - Pos-8)));
00165                     Burnup_Pos = file_pos;   // Position in the file of the closest burnup to burnup_MWd_kgU
00166                     Infile_burnup = strLine.substr(Pos+8, strLine.size() -15 - Pos-8);
00167                     new_format = true;
00168                 }
00169             }
00170         }
00171     }
00172 
00173     int length_mat = 7;
00174     if(new_format) {length_mat =6;}
00175     
00176     // Check if the fuel with this burnup already exists
00177     if (fFuelBurnupMWd_kgU.find(Infile_burnup) != fFuelBurnupMWd_kgU.end()) {
00178         return &(fFuelBurnupMWd_kgU[Infile_burnup]);
00179     }
00180     else {
00181         // Come back to the position of the closest burnup
00182         myfile.clear();
00183         myfile.seekg (Burnup_Pos, myfile.beg);
00184 //         while(std::getline (myfile,strLine) && strLine.front() != 'c') { // Does not work properly if one isotope is commented
00185         while(std::getline (myfile,strLine)  // We read the file until we can't
00186             && ( new_format                  // and with the new format 
00187               || (strLine.size() > 12        // or with the first format we do not reach the next fuel element
00188                 && strLine.substr(0,12) != "c ----- Fuel")
00189               || strLine.size() <= 12)
00190             ) {
00191 
00192             
00193             if(  strLine.front() == 'm' 
00194             && ((strLine.at(strLine.size()-2) == '1' && !new_format)
00195                 || (strLine.at(5) == '3' && new_format))              
00196             ) { // if new material ending by 1 => new layer or 3 with the new format
00197                 if(bool_atomic_fraction) {  // If element were given with atom fraction, add the elements by mass fraction before adding a new material
00198                     AddAtomicFractions( fFuelBurnupMWd_kgU[Infile_burnup].back().back() , &V_nAtoms);
00199                     bool_atomic_fraction = false;
00200                 }
00201                 fFuelBurnupMWd_kgU[Infile_burnup].push_back( { new TMXMaterial( ("fuel_UO2Al_" + strLine.substr(0,length_mat)),
00202                                                             4.71983*(g/cm3), 
00203                                                             GetNumberOfElements(myfile, myfile.tellg(), new_format),
00204                                                             kStateSolid, 
00205                                                             ftemperature_fuel) } );
00206 
00207                 if(new_format) {
00208                     AddElementByMassOrAtom( strLine, bool_atomic_fraction, fFuelBurnupMWd_kgU[Infile_burnup].back().back(), &V_nAtoms);
00209                 }
00210             }
00211             else if (strLine.front() == 'm' ||
00212                 (new_format                  // with the new format 
00213                 && strLine.size() > 12      
00214                 && strLine.substr(0,12) == "c ----- Fuel")
00215             ) { // if new material not ending by 1 => new slice in the horizontal plane
00216 
00217                 if(new_format                  // with the new format
00218                 && strLine.size() > 12      
00219                 && strLine.substr(0,12) == "c ----- Fuel") {
00220                     std::getline (myfile,strLine);
00221                     std::getline (myfile,strLine);
00222                 }
00223                 
00224                 if(strLine.at(5) == '3' && new_format) { break;}
00225 
00226                 if(bool_atomic_fraction) { 
00227                     AddAtomicFractions( fFuelBurnupMWd_kgU[Infile_burnup].back().back() , &V_nAtoms);
00228                     bool_atomic_fraction = false;
00229                 }
00230 //                 fFuelBurnupMWd_kgU[Infile_burnup].back().push_back( new TMXMaterial( ("fuel_UO2Al_" + strLine).c_str(),
00231                 fFuelBurnupMWd_kgU[Infile_burnup].back().push_back( new TMXMaterial( ("fuel_UO2Al_" + strLine.substr(0,length_mat)),
00232                                                                 4.71983*(g/cm3),
00233                                                                 GetNumberOfElements(myfile, myfile.tellg(), new_format),
00234                                                                 kStateSolid,
00235                                                                 ftemperature_fuel ) );
00236                 if(new_format) {
00237                     AddElementByMassOrAtom( strLine, bool_atomic_fraction, fFuelBurnupMWd_kgU[Infile_burnup].back().back(), &V_nAtoms);
00238                 }
00239             }
00240             else { // New elements in the layer x and the slice y
00241                 AddElementByMassOrAtom( strLine, bool_atomic_fraction, fFuelBurnupMWd_kgU[Infile_burnup].back().back(), &V_nAtoms);
00242             }
00243         }
00244         
00245         if(bool_atomic_fraction) {  // If element were given with atom fraction, add the elements by mass fraction before adding a new material
00246             AddAtomicFractions( fFuelBurnupMWd_kgU[Infile_burnup].back().back() , &V_nAtoms);
00247             bool_atomic_fraction = false;
00248         }
00249         
00250         myfile.close();    
00251         
00252         return &(fFuelBurnupMWd_kgU[Infile_burnup]);
00253     }
00254 }
00255 
00256 int DefineMaterials2011::GetNumberOfElements(std::ifstream& myfile, size_t pos_begin, bool new_format) {
00257     int Nelem = 0; // Counter of elements ( = lines)
00258     std::string strLine;
00259     
00260     myfile.seekg (pos_begin, myfile.beg); // Go to the start position
00261     while(std::getline (myfile,strLine)  // We read the file until we can't or we reach the next layer
00262            && strLine.size() > 12 
00263            && strLine.substr(0,12) != "c ----- Fuel"
00264            && strLine.front() != 'm') // Look for a new material
00265     {
00266         if(strLine.find("6000.", 0) != std::string::npos) {
00267         ++Nelem;
00268         }
00269         ++Nelem;
00270     }
00271     if(new_format)  ++Nelem;
00272     myfile.clear();
00273     myfile.seekg (pos_begin, myfile.beg); // go back to the start position
00274     
00275     return Nelem;
00276 }
00277 
00278 void DefineMaterials2011::AddElementByMassOrAtom( std::string strLine, bool &bool_atomic_fraction, TMXMaterial* mat, std::vector<std::pair<int, G4double>>* V_nAtoms) {
00279     std::string IZA = strLine.substr(strLine.find(".", 0)-5, 5) + "0"; // Extract IZA number
00280     double Fraction_value = std::stod( strLine.substr(strLine.find(".", 0)+4, strLine.size() - 4 - strLine.find(".", 0)) ); // Atomic or mass fraction
00281     
00282     if(Fraction_value < 0) { // If mass fraction
00283         mat->AddElement(std::atoi(IZA.c_str()), Fraction_value);}
00284     else { // if atomic fraction => the mass fraction is calculated before adding a new material
00285         bool_atomic_fraction = true;
00286         V_nAtoms->push_back( {std::atoi(IZA.c_str()) , Fraction_value} );
00287     }    
00288 }
