00301 void DefineMaterials2011::DefineUniverseMaterial()
00302 {
00304     // Universe Air (Vacuum)
00306     fUniverseMaterial = TMXMaterial::Find("universe_world_air");
00307     if( !fUniverseMaterial ) {
00308         fUniverseMaterial = new TMXMaterial("universe_world_air",universe_mean_density*g/cm3, 1, kStateGas, ftemperature_world, 3.e-18*hep_pascal);
00309         fUniverseMaterial->AddElement(10010,1);
00310     }
00311 }
