00313 void DefineMaterials2011::DefineAirMaterial()
00314 {
00316   // Air
00318 // c -----  Air, 0.001293  ----------
00319 // m5      7014.70c    -0.75      $ N
00320 //         8016.70c    -0.25      $ O
00321 // c
00322   
00323     fAirMaterial = new TMXMaterial("world_air_mat", 0.001293*(g/cm3), 2, kStateGas, ftemperature_PrimaryCoolantOutOfCore);
00324     fAirMaterial->AddElement(70140,0.75);
00325     fAirMaterial->AddElement(80160,0.25);
00326 }
