00511 void DefineMaterials2011::DefineFuelAbsorMaterial()
00512 {
00514   // B4C
00516 
00517 //     // second version of MCNP model
00518 // // c ----- B4C (100hm.% B10), 2.530 g/cm3
00519 // // m7     5010.70c 4   $    B10
00520 // //        6000.70c 1   $    C
00521 //   G4int nAtoms;
00522 //   fFuelAbsorMaterial = new TMXMaterial("fuel_B4C",2.53*(g/cm3), 2, kStateSolid, ftemperature_PrimaryCoolantInCore);
00523 //   fFuelAbsorMaterial->AddElement( 50100, nAtoms = 4);
00524 //   fFuelAbsorMaterial->AddElement( 60120, nAtoms = 1);
00525 
00526 //   // First version of MCNP model sent by CVR
00527 //   commented on 17/06/2021
