00534 //  fFuelAbsorMaterial->AddElement( 50100, nAtoms = 199);
00535 //  fFuelAbsorMaterial->AddElement( 50110, nAtoms = 801);
00536 //  fFuelAbsorMaterial->AddElement( 60120, nAtoms = 250);
00537 
00538     G4int nAtoms;
