00547 void DefineMaterials2011::DefineReactorMaterial()
00548 {
00550     // Reactor vessel material
00552 
00553     // c -----  Reactor vessel, stainless steel, 7.560663 g/cm3  -------
00554     // m6     6000.70c  -0.0012             $ C
00555     //       14028.70c  -0.009223           $ Si
00556     //       14029.70c  -0.000467
00557     //       14030.70c  -0.00031
00558     //       15031.70c  -0.00035            $ P
00559     //       16032.70c  -0.00033257         $ S
00560     //       16033.70c  -0.000002625
00561     //       16034.70c  -0.000014735
00562     //       16036.70c  -0.00000007
00563     //       22046.70c  -0.00036            $ Ti           
00564     //       22047.70c  -0.0003285
00565     //       22048.70c  -0.003321
00566     //       22049.70c  -0.0002475
00567     //       22050.70c  -0.000243
00568     //       24050.70c  -0.008038           $ Cr
00569     //       24052.70c  -0.155010
00570     //       24053.70c  -0.017577
00571     //       24054.70c  -0.004375
00572     //       25055.70c  -0.02               $ Mn
00573     //       26054.70c  -0.039649           $ Fe
00574     //       26056.70c  -0.626998
00575     //       26057.70c  -0.015039
00576     //       26058.70c  -0.001914
00577     //       28058.70c  -0.064673           $ Ni
00578     //       28060.70c  -0.024912
00579     //       28061.70c  -0.001083
00580     //       28062.70c  -0.003452
00581     //       28064.70c  -0.000880
00582     
00583     fReactorMaterial = new TMXMaterial("fuel_vessel_mat", 7.560663*(g/cm3), 29, kStateSolid, ftemperature_PrimaryCoolantOutOfCore);
00584     //   fReactorMaterial->AddElement(C_12,0.0012);
00585     // --->  Isotope:   C12   Z =  6   N =  12   A =  12.00 g/mole   abundance: 98.930 %
00586     // --->  Isotope:   C13   Z =  6   N =  13   A =  13.00 g/mole   abundance:  1.070 %
00587 //     fReactorMaterial->AddElement(G4NistManager::Instance()->FindOrBuildElement( "C" ),0.0012);
00588 //    fReactorMaterial->AddElement( 60120,0.0012);      // C-12
00589     fReactorMaterial->AddElement( 60120,0.0012*0.9884);
00590     fReactorMaterial->AddElement( 60130,0.0012*0.0116);
00591     fReactorMaterial->AddElement(140280,0.009223);    // Si28
00592     fReactorMaterial->AddElement(140290,0.000467);    // Si29
00593     fReactorMaterial->AddElement(140300,0.00031);     // Si30
00594     fReactorMaterial->AddElement(150310,0.00035);     // P_31
00595     fReactorMaterial->AddElement(160320,0.00033257);  // S_32
00596     fReactorMaterial->AddElement(160330,0.000002625); // S_33
00597     fReactorMaterial->AddElement(160340,0.000014735); // S_34
00598     fReactorMaterial->AddElement(160360,0.00000007);  // S_36
00599     fReactorMaterial->AddElement(220460,0.00036);     // Ti46
00600     fReactorMaterial->AddElement(220470,0.0003285);   // Ti47
00601     fReactorMaterial->AddElement(220480,0.003321);    // Ti48
00602     fReactorMaterial->AddElement(220490,0.0002475);   // Ti49
00603     fReactorMaterial->AddElement(220500,0.000243);    // Ti50
00604     fReactorMaterial->AddElement(240500,0.008038);    // Cr50
00605     fReactorMaterial->AddElement(240520,0.155010);    // Cr52
00606     fReactorMaterial->AddElement(240530,0.017577);    // Cr53
00607     fReactorMaterial->AddElement(240540,0.004375);    // Cr54
00608     fReactorMaterial->AddElement(250550,0.02);        // Mn55
00609     fReactorMaterial->AddElement(260540,0.039649);    // Fe54
00610     fReactorMaterial->AddElement(260560,0.626998);    // Fe56
