00628 void DefineMaterials2011::DefineFuelReflMaterial4()
00629 {
00631     // Berylium
00633     // c ----- Be, 1.85 g/cm3
00634     // m4      1001.70c 8.565E-05
00635     //       1002.70c 1.545E-07
00636     //       1003.70c 1.659E-04
00637     //       2003.70c 5.109E-06
00638     //       2004.70c 8.891E-04
00639     //       3006.70c 1.204E-05
00640     //       3007.70c 1.374E-07
00641     //       4009.70c 9.988E-01
00642     //       5010.70c 3.795E-11
00643     //       5011.70c 1.185E-09
00644     //       6012     3.209E-14
00645     //       6013     3.838E-19
00646     // mt4    be.10t              $ 294 K
00647     //   G4Material* fuel_Berylium_m4 = new G4Material("fuel_Berylium_m4",1.85*(g/cm3), 12, kStateSolid, ftemperature_PrimaryCoolantInCore);
00648     fFuelReflMaterial4.push_back( new TMXMaterial("fuel_Berylium_m4",1.85*(g/cm3), 11, kStateSolid, ftemperature_PrimaryCoolantAndReflectorAndCR));
00649     
00650     // Atomic fraction
00651     std::vector<std::pair<int, G4double> > V_nAtoms4 { {10010,8.565E-05}, // 1001
00652     {10020, 1.545E-07}, // 1002
00653     {10030, 1.659E-04}, // 1003
00654     {20030, 5.109E-06}, // 2003
00655     {20040, 8.891E-04}, // 2004
00656     {30060, 1.204E-05}, // 3006
00657     {30070, 1.374E-07}, // 3007
00658     {40090, 9.988E-01}, // 4009
00659     {50100, 3.795E-11}, // 5010
00660     {50110, 1.185E-09}, // 5011
00661     {60120, 3.209E-14}, // 6012
00662     /*{60130, 3.838E-19}*/};// 6013
00663 
00664     G4double M_tot = 0; // Total mass * Na
00665     for(size_t i = 0; i< V_nAtoms4.size(); ++i) {
00666         M_tot += V_nAtoms4[i].second * TMXElementStore::GetElement(V_nAtoms4[i].first)->Mweight()*(g/mole);
00667     }
00668 
00669     for(size_t i = 0; i< V_nAtoms4.size(); ++i) {
00670         if(V_nAtoms4[i].first == 40090) { fFuelReflMaterial4.back()->AddElement(V_nAtoms4[i].first, V_nAtoms4[i].second * TMXElementStore::GetElement(V_nAtoms4[i].first)->Mweight()*(g/mole) / M_tot, "TS_Beryllium_Metal"); }
00671         else { fFuelReflMaterial4.back()->AddElement(V_nAtoms4[i].first, V_nAtoms4[i].second * TMXElementStore::GetElement(V_nAtoms4[i].first)->Mweight()*(g/mole) / M_tot); }
00672     }
00673 
00674     // m41      1001.70c 1.346E-04
00675     //       1002.70c 3.303E-07
00676     //       1003.70c 2.563E-04
00677     //       2003.70c 7.297E-06
00678     //       2004.70c 1.347E-03
00679     //       3006.70c 1.337E-05
00680     //       3007.70c 2.125E-07
00681     //       4009.70c 9.982E-01
00682     //       5010.70c 4.593E-11
00683     //       5011.70c 2.232E-09
00684     //       6012     8.245E-14
00685     //       6013     1.339E-18
00686     // mt41    be.10t              $ 294 K
