00948   fMixCm_Material->AddElement(962430,  1807);
00949   fMixCm_Material->AddElement(962440, 90860);
00950   fMixCm_Material->AddElement(962450,  6569);
00951   fMixCm_Material->AddElement(962460,   745);
00952   fMixCm_Material->AddElement(962470,     7);
00953 }
00954 
00955 void DefineMaterials2011::DefineDirtPu_Material()
00956 {
00957   if(!fRecycledPu_Material) {DefineRecycledPu_Material();}
00958   if(!fMixAm_Material)      {DefineMixAm_Material();}
00959   if(!fMixCm_Material)      {DefineMixCm_Material();}
00960     
00961   G4double x_mass_Np = 0.053597;
00962   G4double x_mass_Pu = 0.860810;
00963   G4double x_mass_Am = 0.083738;
00964   G4double x_mass_Cm = 0.001855;
00965 
00966   G4double denDirtPu = 11*CLHEP::g/CLHEP::cm3;
00967   fDirtPu_Material = new TMXMaterial("fuel_DirtPu", denDirtPu, 4, kStateSolid, ftemperature_fuel);
00968   fDirtPu_Material->AddElement (932370              , x_mass_Np);
00969   fDirtPu_Material->AddMaterial(fRecycledPu_Material, x_mass_Pu);
00970   fDirtPu_Material->AddMaterial(fMixAm_Material     , x_mass_Am);
00971   fDirtPu_Material->AddMaterial(fMixCm_Material     , x_mass_Cm);
00972 }
00973 
00975   // ThPuO2
00977   /*
00978    *     6.  ThPu-O2 (=10.407g/cc): typical start-up fuel for the Energy Amplifier
00979          Mlange = Th-232 0.87 + mlange Dirty Pu 0.13 + O 2.0 (at.%)
00980    */
00981 void DefineMaterials2011::DefineThPuO2_Material()
00982 {
00983   if(!fDirtPu_Material) {DefineDirtPu_Material();}
00984   double MolarMass_Th232 = (TMXElementStore::GetElement(902320))->Mweight();
00985   double MolarMass_O16   = (TMXElementStore::GetElement( 80160))->Mweight();
00986   double MolarMass_DirtyPu = GetMolarMass(fDirtPu_Material);
00987   double MolarMass = 0.13 * MolarMass_DirtyPu + 0.87 * MolarMass_Th232 + 2.00 * MolarMass_O16; // Actually 3 times the molar mass
00988   
00989   G4double denThPuO2 = 10.407*CLHEP::g/CLHEP::cm3;
00990   fThPuO2_Material = new TMXMaterial("fuel_ThPuO2", denThPuO2, 3, kStateSolid, ftemperature_fuel);
00991   fThPuO2_Material->AddMaterial(fDirtPu_Material, 0.13 * MolarMass_DirtyPu / MolarMass);
00992   fThPuO2_Material->AddElement(902320           , 0.87 * MolarMass_Th232   / MolarMass);
00993   fThPuO2_Material->AddElement( 80160           , 2.00 * MolarMass_O16     / MolarMass);
00994 }
