01123     
01127 //      Tools
01128 //
01131 
01133 //  Return the molar mass in g/mole of a material composed of one single isotope per element
01134 //
01135 double DefineMaterials2011::GetMolarMass(TMXMaterial* mat) {
01136     const G4ElementVector * elemVector = mat->GetElementVector();
01137     const double* AtomsVectorPerVolume = mat->GetVecNbOfAtomsPerVolume();
01138     double TotNbAtoms = mat->GetTotNbOfAtomsPerVolume();
01139     
01140     double MolarMass = 0;
01141     for(size_t elem=0; elem < elemVector->size(); ++elem) {
01142         MolarMass += AtomsVectorPerVolume[elem]/TotNbAtoms  * (*elemVector)[elem]->GetA()/(g/mole);
01143     }
01144     return MolarMass;
01145 }
01146 
01148 //  Fill the material with element by mass fraction calculated from a vector of IZA and atom fraction (or number of atoms)
01149 //
01150 void DefineMaterials2011::AddAtomicFractions( TMXMaterial* mat, std::vector<std::pair<int, G4double>>* V_nAtoms) {
01151 
01152   // Calculation of the total mass
01153   G4double M_tot = 0; // Total mass * Na
01154 //  G4bool split = false;
01155 //  int count = 0;
01156   for(size_t i = 0; i< V_nAtoms->size(); ++i) {      
01157       if ((*V_nAtoms)[i].first  == 60000)
01158       {
01159 //          count += 2;
01160           M_tot += (*V_nAtoms)[i].second * 0.9884 * (TMXElementStore::GetElement(60120)->Mweight()*(g/mole));
01161           M_tot += (*V_nAtoms)[i].second * 0.0116 * (TMXElementStore::GetElement(60130)->Mweight()*(g/mole));
01162 //          split = true;
01163       }
01164       else
01165       {
01166           M_tot += (*V_nAtoms)[i].second * (TMXElementStore::GetElement((*V_nAtoms)[i].first)->Mweight()*(g/mole));
01167 //          count++;
01168       }
01169   }
01170 
01171 //  if(split)
01172 //  {
01173 //      TMXMaterial *mat2 = new TMXMaterial(mat->GetName(), mat->GetDensity(), count, mat->GetState(), mat->GetTemperature(), mat->GetPressure());
01174 //  }
01175 
01176   // Addition of elements via its mass fraction
01177   for(size_t i = 0; i< V_nAtoms->size(); ++i) {
01178       if ((*V_nAtoms)[i].first  == 60000)
01179       {
01180           mat->AddElement(60120, (*V_nAtoms)[i].second * (TMXElementStore::GetElement(60120)->Mweight()*(g/mole)) / M_tot);
01181           mat->AddElement(60130, (*V_nAtoms)[i].second * (TMXElementStore::GetElement(60130)->Mweight()*(g/mole)) / M_tot);
01182       }
01183       else
01184           mat->AddElement((*V_nAtoms)[i].first, (*V_nAtoms)[i].second * (TMXElementStore::GetElement((*V_nAtoms)[i].first)->Mweight()*(g/mole)) / M_tot);
01185   }
01186 
01187   // Clear the vector
01188   V_nAtoms->clear();
01189 }
