00193 double Burnup::avg(std::vector<double> sample) {
00194     int n = (int)sample.size();
00195     double sum = 0.;
00196     for (int i = 0; i < n; i++) {
00197         sum += sample[i];
00198     }
00199     return(sum/(double)n);
00200 }
