00207 void Burnup::recursiveInitialization(G4VPhysicalVolume* PV, int replicate_number) {
00208     if(PV) // if physical volume exists
00209     {
00210         G4LogicalVolume* LV     = PV->GetLogicalVolume();
00211         int PV_replicate_number = replicate_number * PV->GetMultiplicity(); // Get the number of replicates
00212 
00213         if(LV) // if logical volume exists
00214         {
00215             // For each Physical volume get daugther of the Logical volume
00216             for(size_t i = 0; i < LV->GetNoDaughters(); ++i)
00217             {
00218                 G4VPhysicalVolume* Daughter_PV = LV->GetDaughter(i);
00219                 std::map<TMXMaterial*, std::vector<G4LogicalVolume*>>::iterator mat_it = m_matToLV.find((TMXMaterial*)(LV)->GetMaterial());
00220                 if(mat_it  == m_matToLV.end())
00221                 {
00222                     m_matToLV[(TMXMaterial*)(Daughter_PV->GetLogicalVolume())->GetMaterial()] = {Daughter_PV->GetLogicalVolume()};
00223                     m_matToMass[(TMXMaterial*)(Daughter_PV->GetLogicalVolume())->GetMaterial()] = (Daughter_PV->GetLogicalVolume())->GetMass(true, false) / g;
00224                 }
00225                 else
00226                 {
00227                     m_matToLV[(TMXMaterial*)(Daughter_PV->GetLogicalVolume())->GetMaterial()].push_back(Daughter_PV->GetLogicalVolume());
00228                     m_matToMass[(TMXMaterial*)(Daughter_PV->GetLogicalVolume())->GetMaterial()] += (Daughter_PV->GetLogicalVolume())->GetMass(true, false) / g;
00229                 }
00230                 recursiveInitialization(Daughter_PV, PV_replicate_number);
00231             }
00232         }
00233     }
00234 }
