00239 void Burnup::ModifyMaterial()
00240 {
00241     G4bool hasScalingBeenModified = false;
00242     //Loop on the materials matToLV = (material*, vector<lvolume*>)
00243     for(const auto& mat : m_matToLV)
00244     {
00245         //Compute number of atoms of each nuclide in the material
00246         computetNumOfAtoms(mat.first);
00247 
00248 
00249         //Addition of the decayed source term inside the final concentration + addition of initial compound decay
00250 
00251         //Test to constitute time step
00252         testTimeStep(mat.first, hasScalingBeenModified);
00253     }
00254 
00255 
00257     // Cette partie du code est dégueulasse. TF
00259     if (m_verbose > 0 && hasScalingBeenModified)
00260     {
00261         std::ostringstream streamObj;
00262         streamObj << m_epsilonabs;
00263         std::cout << "The above nuclides are produced or consommed with a ratio above " << m_epsilon << " % and a production above " + streamObj.str() + " atoms\n"        << std::endl;
00264     }
00265     if(hasScalingBeenModified)
00266     {
00267         std::cout << "Scaling factor " << m_scalingFactor << " -> " << m_scalingFactor * m_epsilon / m_scalingCorrection << " particles"                     << std::endl;
00268         std::cout << "TimeStep correction: " << m_currentIrradiationPeriod << " -> " << m_currentIrradiationPeriod * m_epsilon / m_scalingCorrection << " s" << std::endl;
00269         std::cout << "Irradiation time reduction of a factor " << m_scalingCorrection / m_epsilon                                                                          << std::endl;
00270         m_scalingFactor            *= m_epsilon / m_scalingCorrection;
00271 
00272         m_irradiationTimePassed    -= m_currentIrradiationPeriod;
00273         m_currentIrradiationPeriod *= m_epsilon / m_scalingCorrection;
00274         m_irradiationTimePassed    += m_currentIrradiationPeriod;
00275 
00276         std::cout << "\n****************************************************"                  << std::endl;
00277         std::cout << "************ TIME STEP HAS BEEN CHANGED ************"                    << std::endl;
00278         std::cout << "****************************************************"                    << std::endl;
00279 
00280     }
00281     if(m_irradiationTimePassed > m_totalIrradiationPeriod)
00282     {
00283         m_irradiationTimePassed    += -(m_irradiationTimePassed - m_totalIrradiationPeriod);
00284         m_currentIrradiationPeriod += -(m_irradiationTimePassed - m_totalIrradiationPeriod);
00285 
00286         m_scalingFactor            = m_currentIrradiationPeriod * m_beamIntensity / (m_nParticles * e_SI);
00287     }
00288 
00289 
00290     if(m_verbose > 0)
00291     {
00292         std::cout << "****************************************************"                    << std::endl;
00293         std::cout << "********** BEAM PARAMETERS FOR ACTIVATION **********"                    << std::endl;
00294         std::cout << "****************************************************\n"                  << std::endl;
00295 
00296         std::cout << "* Core power:                " << m_corePower << " MW"                   << std::endl;
00297         std::cout << "* Beam power:                " << m_beamPower << " MW"                   << std::endl;
00298         std::cout << "* Beam energy:               " << m_beamEnergy << " MeV"                 << std::endl;
00299         std::cout << "* Beam intensity:            " << m_beamIntensity * 1000 << " mA"        << std::endl;
00300         std::cout << "* Step irradiation period:   " << m_currentIrradiationPeriod << " s " << " ("
00301                   << m_currentIrradiationPeriod / m_totalIrradiationPeriod * 100 << " %)"      << std::endl;
00302         std::cout << "* Step scaling factor:       " << m_scalingFactor
00303                   << " number of primaries during step irradiation period"                     << std::endl;
00304         std::cout << "* Current irradiation time:  " << m_irradiationTimePassed << " s "
00305                   << " (" << m_irradiationTimePassed / m_totalIrradiationPeriod * 100
00306                                                                                      << " %)"  << std::endl;
00307         std::cout << "\n****************************************************"                  << std::endl;
00308         std::cout << "**************** MATERIAL ACTIVATION ***************"                    << std::endl;
00309         std::cout << "****************************************************\n"                  << std::endl;
00310     }
00312 
00313 
00314 
00315     //Loop on the materials matToLV = (material*, vector<lvolume*>)
00316     for(const auto& mat : m_matToLV)
00317     {
00318         if(m_verbose > 1) printMaterial(mat.first);
00319 
00320         // Computing the new nuclide vector and building new material
00321         computeNewNuclideVector(mat.first);
00322 
00323         TMXMaterial * newMat = buildMaterial(mat.first);
00324 
00325         // Replacing materials in all the LogicalVolumes
00326         for(std::vector<G4LogicalVolume*>::size_type i=0; i<mat.second.size(); i++)
00327             mat.second[i]->SetMaterial(newMat);
00328 
00329         if(m_verbose > 1) printMaterial((TMXMaterial*)newMat);
00330     }
00331 }
