00337 void Burnup::computeNewNuclideVector(TMXMaterial* mat)
00338 {
00339     //Compute number of atoms of each nuclide in the material
00340     computetNumOfAtoms(mat);
00341 
00343     //Here the bateman decay has to be done//
00345 
00346     //Addition of the decayed source term inside the final concentration + addition of initial compound decay
00347     TMXBatemanDecay bateman;
00348     bateman.decayNuclideVector(m_nuclideVector, m_sourceTerm[mat],
00349                                m_currentIrradiationPeriod,
00350                                m_scalingFactor);
00351 
00352 }
00353 
00358 void Burnup::testTimeStep(TMXMaterial* mat, G4bool &modifScaling)
00359 {
00360     for(const auto& source : m_sourceTerm[mat])
00361     {
00362         if(m_nuclideVector[source.first] != 0. && source.second != 0.)
00363         {
00364             if( (std::abs(source.second * m_scalingFactor / m_nuclideVector[source.first] * 100) > m_epsilon
00365                     && m_nuclideVector[source.first] > m_epsilonabs))
00366             {
00367                 if(m_verbose > 0) std::cout << mat->GetName() << " " << source.first
00368                                                                              << " " << source.second * m_scalingFactor / m_nuclideVector[source.first] * 100 << " % and "
00369                                                                              << m_nuclideVector[source.first] << " number of atoms" << std::endl;
00370                 m_scalingCorrection = std::max(m_scalingCorrection, std::abs(source.second * m_scalingFactor / m_nuclideVector[source.first] * 100));
00371                 modifScaling = true;
00372             }
00373             if(m_nuclideVector[source.first] + source.second * m_scalingFactor < 0. )
00374             {
00375                 double newScalingFactor = -m_nuclideVector[source.first] / source.second;
00376                 if(newScalingFactor <= m_scalingFactor * m_epsilon / m_scalingCorrection)
00377                 {
00378                     double timeToSubstract      = m_currentIrradiationPeriod * ( 1 - newScalingFactor / m_scalingFactor );
00379                     m_currentIrradiationPeriod += - timeToSubstract;
00380                     m_irradiationTimePassed    += - timeToSubstract;
00381                     m_scalingFactor             = newScalingFactor;
00382                     std::cout<< "Consumption of the totality of " << source.first << ". Initial number of atoms: " << m_nuclideVector[source.first]
00383                              << ". Final number of atoms: " << m_nuclideVector[source.first] + source.second * m_scalingFactor<<std::endl;
00384                 }
00385             }
00386         }
00387     }
00388 }
00389 
00394 void Burnup::computetNumOfAtoms(TMXMaterial* mat)
00395 {
00396     m_nuclideVector.clear();
00397     const G4double* massFraction    = mat->GetFractionVector();
00398 
00399     //Loop on the number of elements of the material
00400     for(unsigned int j=0; j < mat->GetNumberOfElements(); ++j)
00401     {
00402         G4double* relativeAbundance = mat->GetElement(j)->GetRelativeAbundanceVector();
00403 
00404         //Loop on the number of isotopes of the element
00405         for(unsigned int i = 0; i <  mat->GetElement(j)->GetNumberOfIsotopes(); i++)
00406         {
00407             G4double nAtoms = m_matToMass[mat] * CLHEP::Avogadro / ( mat->GetElement(j)->GetA() / (g/mole) ) * relativeAbundance[i] * massFraction[j];// tenir compte des différents volumes
00408             double IZA = mat->GetElement(j)->GetZ() * 10000 + mat->GetElement(j)->GetIsotope(0)->GetN() * 10 + mat->GetElement(j)->GetIsotope(0)->Getm();
00409             std::map<G4int,G4double>::iterator it = m_nuclideVector.find(IZA);
00410             if ( it == m_nuclideVector.end())
00411                 m_nuclideVector[IZA] = nAtoms;
00412             else
00413                 m_nuclideVector[IZA] += nAtoms;
00414         }
00415     }
00416 }
00417 
00423 TMXMaterial* Burnup::buildMaterial(TMXMaterial* mat)
00424 {
00425     std::vector<int> remove;
00426     for(auto const& elem : m_nuclideVector)
00427         if (elem.second <= 1. )
00428             remove.push_back(elem.first);
00429     for(auto &e: remove) m_nuclideVector.erase(e);
00430 
00431     //Compute mass of the new material and turn nuclide vector from atom percentage to mass percentage
00432     G4double newMass       = getMaterialMass();
00433 
00434     //Initialize a new material with the new composition
00435     TMXMaterial *updatedMat = new TMXMaterial(getMatNameCurrentStep(mat),
00436                                             mat->GetDensity() * newMass / m_matToMass[mat],
00437                                             m_nuclideVector.size(),
00438                                             mat->GetState(),
00439                                             mat->GetTemperature(),
00440                                             mat->GetPressure());
00441     //Construct the new material
00442     for(auto const& elem : m_nuclideVector)
00443     {
00444         if( TMXElementStore::GetElement(elem.first)->Name().substr(0,3) == "TS_")
00445         {
00446             updatedMat->AddElement(TMXElementStore::GetElement(elem.first)->IZA(), elem.second / newMass, TMXElementStore::GetElement(elem.first)->Name());
00447         }
00448         else
00449         {
00450 //            std::cout<< elem.first<< " " <<elem.second <<std::endl;
00451 //            std::cout<<TMXElementStore::GetElement(elem.first)->IZA() << " "<<elem.second / newMass <<std::endl;
00452             if (elem.second / newMass <= 0.) // ATTENTION: on triche
00453                 updatedMat->AddElement(TMXElementStore::GetElement(elem.first)->IZA(), 0.);
00454             else
00455                 updatedMat->AddElement(TMXElementStore::GetElement(elem.first)->IZA(), elem.second / newMass);
00456 
00457         }
00458     }
00459     return updatedMat;
00460 }
00461 
00467 G4double Burnup::getMaterialMass()
00468 {
00469     G4double newMass = 0.;
00470     // Loop over nuclides in nuclideVector
00471     // isot.second returns the number of atoms of nuclide with name isot.first
00472     for(const auto& isot : m_nuclideVector)
00473     {
00474         if(TMXElementStore::GetElement(isot.first) == NULL)
00475         {
00476             std::cout << "Element " << isot.first << " unknown" << std::endl;
00477         }
00478         G4double isotMass = isot.second / CLHEP::Avogadro * TMXElementStore::GetElement(isot.first)->Mweight();
00479         newMass += isotMass;
00480 //        std::cout<< isot.first<< " MassIsot: "<<isotMass <<" MassMat: "<< newMass<<" nAtomsIsot: "<< isot.second <<std::endl;
00481         m_nuclideVector[isot.first] = isotMass;
00482     }
00483     return newMass;
00484 }
00485 
00491 std::string Burnup::getMatNameCurrentStep(TMXMaterial *mat)
00492 {
00493     return (mat->GetName().find("*") == std::string::npos)
00494                              ? mat->GetName() + "*" + std::to_string(m_currentStep)
00495                              : mat->GetName().substr(0, mat->GetName().find("*")) + "*" + std::to_string(m_currentStep);
00496 }
00497 
00504 std::string Burnup::getMatName(TMXMaterial *mat, int nStep)
00505 {
00506     if (nStep == 0)
00507         return mat->GetName().substr(0, mat->GetName().find("*"));
00508     else
00509         return mat->GetName().substr(0, mat->GetName().find("*")) + "*" + std::to_string(nStep);
00510 }
